// frontend/src/components/HR/HRDashboard.jsx (‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï)
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/hr.css';

const HRDashboard = () => {
  const [companyInfo, setCompanyInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [jobStats, setJobStats] = useState({
    total: 0,
    active: 0,
    applications: 0,
    qualified: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');
      
      // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡πÅ‡∏•‡∏∞‡∏™‡∏ñ‡∏¥‡∏ï‡∏¥
      const promises = [
        fetch('/api/profile/company-info', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }),
        fetch('/api/jobs/statistics/overview', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        })
      ];

      const [companyResponse, jobStatsResponse] = await Promise.allSettled(promises);

      // ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå company
      if (companyResponse.status === 'fulfilled' && companyResponse.value.ok) {
        const contentType = companyResponse.value.headers.get('content-type');
        if (contentType && contentType.includes('application/json')) {
          const companyData = await companyResponse.value.json();
          setCompanyInfo(companyData);
        }
      } else {
        console.log('Company API not available, using mock data');
        // ‡πÉ‡∏ä‡πâ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• mock ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö HR ‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó
        setCompanyInfo({
          user_type: 'HR',
          company: {
            name: 'Digital Solutions Ltd.',
            industry: 'Software Development',
            size: 'Medium',
            description: '‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏û‡∏±‡∏í‡∏ô‡∏≤‡∏ã‡∏≠‡∏ü‡∏ï‡πå‡πÅ‡∏ß‡∏£‡πå‡∏ä‡∏±‡πâ‡∏ô‡∏ô‡∏≥'
          }
        });
      }

      // ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå job stats
      if (jobStatsResponse.status === 'fulfilled' && jobStatsResponse.value.ok) {
        const contentType = jobStatsResponse.value.headers.get('content-type');
        if (contentType && contentType.includes('application/json')) {
          const statsData = await jobStatsResponse.value.json();
          setJobStats({
            total: statsData.total_internships || 0,
            active: statsData.active_internships || 0,
            applications: statsData.total_applications || 0,
            qualified: statsData.qualified_applications || 0
          });
        }
      } else {
        console.log('Job stats API not available, using mock data');
        setJobStats({ total: 0, active: 0, applications: 0, qualified: 0 });
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      // ‡πÉ‡∏ä‡πâ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• default ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
      setCompanyInfo({
        user_type: 'HR',
        company: {
          name: 'Digital Solutions Ltd.',
          industry: 'Software Development',
          size: 'Medium',
          description: '‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏û‡∏±‡∏í‡∏ô‡∏≤‡∏ã‡∏≠‡∏ü‡∏ï‡πå‡πÅ‡∏ß‡∏£‡πå‡∏ä‡∏±‡πâ‡∏ô‡∏ô‡∏≥'
        }
      });
      setJobStats({ total: 0, active: 0, applications: 0, qualified: 0 });
    } finally {
      setLoading(false);
    }
  };

  const handleNavigateToJobs = () => {
    navigate('/hr/jobs');
  };

  const handleCreateJob = () => {
    navigate('/hr/jobs/create');
  };

  const handleViewApplications = () => {
    navigate('/hr/applications');
  };

  const handleViewCompanies = () => {
    navigate('/hr/companies');
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•...</p>
      </div>
    );
  }

  return (
    <div className="hr-dashboard">
      <div className="dashboard-container">
        {/* Welcome Section */}
        <div className="welcome-section">
          <div className="welcome-content">
            <h1>‡∏¢‡∏¥‡∏ô‡∏î‡∏µ‡∏ï‡πâ‡∏≠‡∏ô‡∏£‡∏±‡∏ö HR Manager</h1>
            <p>‡∏£‡∏∞‡∏ö‡∏ö‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö HR</p>
            {companyInfo && companyInfo.company && (
              <div className="company-info">
                <div className="company-badge">
                  <span className="company-logo">üè¢</span>
                  <div className="company-details">
                    <h3>{companyInfo.company.name}</h3>
                    <p>{companyInfo.company.industry}</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Quick Stats */}
        {companyInfo && companyInfo.company && (
          <div className="stats-section">
            <div className="stats-grid">
              <div className="stat-card" onClick={handleNavigateToJobs}>
                <div className="stat-icon">üìä</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.total}</div>
                  <div className="stat-label">‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</div>
                </div>
                <div className="stat-arrow">‚Üí</div>
              </div>
              
              <div className="stat-card" onClick={handleNavigateToJobs}>
                <div className="stat-icon">üü¢</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.active}</div>
                  <div className="stat-label">‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</div>
                </div>
                <div className="stat-arrow">‚Üí</div>
              </div>
              
              <div className="stat-card" onClick={handleViewApplications}>
                <div className="stat-icon">üìù</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.applications}</div>
                  <div className="stat-label">‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</div>
                </div>
                <div className="stat-arrow">‚Üí</div>
              </div>
              
              <div className="stat-card" onClick={handleViewApplications}>
                <div className="stat-icon">‚úÖ</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.qualified}</div>
