// frontend/src/components/HR/CreateJobForm.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/CreateJobForm.css';

const CreateJobForm = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    requirements: '',
    responsibilities: '',
    department: '',
    internship_type: 'full-time',
    duration_months: 3,
    positions_available: 1,
    compensation_type: 'paid',
    compensation_amount: '',
    application_deadline: '',
    start_date: '',
    end_date: '',
    location: '',
    work_mode: 'onsite',
    required_skills: [],
    preferred_skills: [],
    education_requirements: '',
    experience_requirements: '',
    additional_requirements: ''
  });

  const [skillInput, setSkillInput] = useState('');
  const [preferredSkillInput, setPreferredSkillInput] = useState('');

  const departmentOptions = [
    'software_development', 'data_science', 'design', 'marketing', 
    'business_development', 'finance', 'human_resources', 'operations', 'other'
  ];

  const internshipTypes = [
    { value: 'full-time', label: 'เต็มเวลา' },
    { value: 'part-time', label: 'ชั่วคราว' },
    { value: 'project-based', label: 'ตามโปรเจค' }
  ];

  const compensationTypes = [
    { value: 'paid', label: 'มีเงินเดือน' },
    { value: 'unpaid', label: 'ไม่มีเงินเดือน' },
    { value: 'allowance', label: 'ค่าใช้จ่าย' }
  ];

  const workModes = [
    { value: 'onsite', label: 'ทำงานที่ออฟฟิศ' },
    { value: 'remote', label: 'ทำงานจากที่บ้าน' },
    { value: 'hybrid', label: 'ผสมผสาน' }
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSkillAdd = (type) => {
    const input = type === 'required' ? skillInput : preferredSkillInput;
    if (input.trim()) {
      setFormData(prev => ({
        ...prev,
        [type === 'required' ? 'required_skills' : 'preferred_skills']: [
          ...prev[type === 'required' ? 'required_skills' : 'preferred_skills'],
          input.trim()
        ]
      }));
      
      if (type === 'required') {
        setSkillInput('');
      } else {
        setPreferredSkillInput('');
      }
    }
  };

  const handleSkillRemove = (type, index) => {
    setFormData(prev => ({
      ...prev,
      [type === 'required' ? 'required_skills' : 'preferred_skills']: 
        prev[type === 'required' ? 'required_skills' : 'preferred_skills'].filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/jobs', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error('Failed to create job');
      }

      const result = await response.json();
      navigate('/hr/jobs', { 
        state: { message: 'สร้างตำแหน่งงานเรียบร้อยแล้ว' }
      });
    } catch (error) {
      console.error('Error creating job:', error);
      alert('เกิดข้อผิดพลาดในการสร้างตำแหน่งงาน');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (window.confirm('คุณต้องการยกเลิกการสร้างตำแหน่งงานหรือไม่?')) {
      navigate('/hr/jobs');
    }
  };

  return (
    <div className="create-job-form">
      <div className="form-header">
        <div className="header-content">
          <h1>สร้างตำแหน่งฝึกงานใหม่</h1>
          <p>กรอกข้อมูลตำแหน่งงานที่ต้องการเปิดรับสมัคร</p>
        </div>
        <div className="header-actions">
          <button 
            type="button" 
            onClick={handleCancel}
            className="secondary-button"
          >
            ยกเลิก
          </button>
          <button 
            type="submit" 
            form="job-form"
            disabled={loading}
            className="primary-button"
          >
            {loading ? 'กำลังบันทึก...' : 'บันทึก'}
          </button>
        </div>
      </div>

      <form id="job-form" onSubmit={handleSubmit} className="job-form">
        {/* ข้อมูลพื้นฐาน */}
        <div className="form-section">
          <div className="section-header">
            <h2>ข้อมูลพื้นฐาน</h2>
            <p>รายละเอียดหลักของตำแหน่งงาน</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label htmlFor="title" className="form-label">
                ชื่อตำแหน่งงาน <span className="required">*</span>
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                className="form-input"
                placeholder="เช่น นักศึกษาฝึกงาน Frontend Developer"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="department" className="form-label">
                แผนก <span className="required">*</span>
              </label>
              <select
                id="department"
                name="department"
                value={formData.department}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                <option value="">เลือกแผนก</option>
                {departmentOptions.map(dept => (
                  <option key={dept} value={dept}>
                    {dept.replace('_', ' ').toUpperCase()}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="internship_type" className="form-label">
                ประเภทการฝึกงาน <span className="required">*</span>
              </label>
              <select
                id="internship_type"
                name="internship_type"
                value={formData.internship_type}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                {internshipTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="duration_months" className="form-label">
                ระยะเวลา (เดือน) <span className="required">*</span>
              </label>
              <input
                type="number"
                id="duration_months"
                name="duration_months"
                value={formData.duration_months}
                onChange={handleInputChange}
                className="form-input"
                min="1"
                max="12"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="positions_available" className="form-label">
                จำนวนที่รับ <span className="required">*</span>
              </label>
              <input
                type="number"
                id="positions_available"
                name="positions_available"
                value={formData.positions_available}
                onChange={handleInputChange}
                className="form-input"
                min="1"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="work_mode" className="form-label">
                รูปแบบการทำงาน <span className="required">*</span>
              </label>
              <select
                id="work_mode"
                name="work_mode"
                value={formData.work_mode}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                {workModes.map(mode => (
                  <option key={mode.value} value={mode.value}>
                    {mode.label}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* คำอธิบายงาน */}
        <div className="form-section">
          <div className="section-header">
            <h2>คำอธิบายงาน</h2>
            <p>รายละเอียดเกี่ยวกับงานและหน้าที่ความรับผิดชอบ</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label htmlFor="description" className="form-label">
                คำอธิบายงาน <span className="required">*</span>
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                className="form-textarea"
                rows="4"
                placeholder="อธิบายลักษณะงานและสิ่งที่นักศึกษาจะได้เรียนรู้..."
                required
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="responsibilities" className="form-label">
                หน้าที่ความรับผิดชอบ <span className="required">*</span>
              </label>
              <textarea
                id="responsibilities"
                name="responsibilities"
                value={formData.responsibilities}
                onChange={handleInputChange}
                className="form-textarea"
                rows="4"
                placeholder="• งานที่ 1&#10;• งานที่ 2&#10;• งานที่ 3..."
                required
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="requirements" className="form-label">
                คุณสมบัติผู้สมัคร <span className="required">*</span>
              </label>
              <textarea
                id="requirements"
                name="requirements"
                value={formData.requirements}
                onChange={handleInputChange}
                className="form-textarea"
                rows="4"
                placeholder="• คุณสมบัติที่ 1&#10;• คุณสมบัติที่ 2&#10;• คุณสมบัติที่ 3..."
                required
              />
            </div>
          </div>
        </div>

        {/* ทักษะที่ต้องการ */}
        <div className="form-section">
          <div className="section-header">
            <h2>ทักษะที่ต้องการ</h2>
            <p>ระบุทักษะที่จำเป็นและทักษะที่ต้องการเพิ่มเติม</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label className="form-label">
                ทักษะที่จำเป็น <span className="required">*</span>
              </label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={skillInput}
                  onChange={(e) => setSkillInput(e.target.value)}
                  className="form-input"
                  placeholder="พิมพ์ทักษะและกด Enter หรือคลิกปุ่มเพิ่ม"
                  onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleSkillAdd('required'))}
                />
                <button
                  type="button"
                  onClick={() => handleSkillAdd('required')}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.required_skills.map((skill, index) => (
                  <div key={index} className="skill-tag">
                    <span>{skill}</span>
                    <button
                      type="button"
                      onClick={() => handleSkillRemove('required', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group full-width">
              <label className="form-label">ทักษะที่ต้องการเพิ่มเติม</label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={preferredSkillInput}
                  onChange={(e) => setPreferredSkillInput(e.target.value)}
                  className="form-input"
                  placeholder="พิมพ์ทักษะและกด Enter หรือคลิกปุ่มเพิ่ม"
                  onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleSkillAdd('preferred'))}
                />
                <button
                  type="button"
                  onClick={() => handleSkillAdd('preferred')}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.preferred_skills.map((skill, index) => (
                  <div key={index} className="skill-tag preferred">
                    <span>{skill}</span>
                    <button
                      type="button"
                      onClick={() => handleSkillRemove('preferred', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* ค่าตอบแทนและสถานที่ */}
        <div className="form-section">
          <div className="section-header">
            <h2>ค่าตอบแทนและสถานที่</h2>
            <p>ข้อมูลเกี่ยวกับค่าตอบแทนและสถานที่ทำงาน</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="compensation_type" className="form-label">
                ประเภทค่าตอบแทน <span className="required">*</span>
              </label>
              <select
                id="compensation_type"
                name="compensation_type"
                value={formData.compensation_type}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                {compensationTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>

            {formData.compensation_type === 'paid' && (
              <div className="form-group">
                <label htmlFor="compensation_amount" className="form-label">
                  จำนวนเงิน (บาท/เดือน)
                </label>
                <input
                  type="number"
                  id="compensation_amount"
                  name="compensation_amount"
                  value={formData.compensation_amount}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="15000"
                  min="0"
                />
              </div>
            )}

            <div className="form-group full-width">
              <label htmlFor="location" className="form-label">
                สถานที่ทำงาน <span className="required">*</span>
              </label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                className="form-input"
                placeholder="เช่น กรุงเทพฯ, เชียงใหม่, หรือ Remote"
                required
              />
            </div>
          </div>
        </div>

        {/* วันที่สำคัญ */}
        <div className="form-section">
          <div className="section-header">
            <h2>วันที่สำคัญ</h2>
            <p>กำหนดวันที่เริ่มต้น สิ้นสุด และหมดเขตรับสมัคร</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="application_deadline" className="form-label">
                วันหมดเขตรับสมัคร
              </label>
              <input
                type="date"
                id="application_deadline"
                name="application_deadline"
                value={formData.application_deadline}
                onChange={handleInputChange}
                className="form-input"
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="start_date" className="form-label">
                วันที่เริ่มฝึกงาน
              </label>
              <input
                type="date"
                id="start_date"
                name="start_date"
                value={formData.start_date}
                onChange={handleInputChange}
                className="form-input"
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="end_date" className="form-label">
                วันที่สิ้นสุดฝึกงาน
              </label>
              <input
                type="date"
                id="end_date"
                name="end_date"
                value={formData.end_date}
                onChange={handleInputChange}
                className="form-input"
                min={formData.start_date || new Date().toISOString().split('T')[0]}
              />
            </div>
          </div>
        </div>

        {/* ข้อมูลเพิ่มเติม */}
        <div className="form-section">
          <div className="section-header">
            <h2>ข้อมูลเพิ่มเติม</h2>
            <p>ข้อมูลเสริมเกี่ยวกับการศึกษาและประสบการณ์</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label htmlFor="education_requirements" className="form-label">
                ข้อกำหนดด้านการศึกษา
              </label>
              <textarea
                id="education_requirements"
                name="education_requirements"
                value={formData.education_requirements}
                onChange={handleInputChange}
                className="form-textarea"
                rows="3"
                placeholder="เช่น กำลังศึกษาระดับปริญญาตรี สาขาวิทยาการคอมพิวเตอร์ หรือสาขาที่เกี่ยวข้อง"
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="experience_requirements" className="form-label">
                ข้อกำหนดด้านประสบการณ์
              </label>
              <textarea
                id="experience_requirements"
                name="experience_requirements"
                value={formData.experience_requirements}
                onChange={handleInputChange}
                className="form-textarea"
                rows="3"
                placeholder="เช่น มีประสบการณ์ในการพัฒนาเว็บไซต์ หรือ ไม่จำเป็นต้องมีประสบการณ์"
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="additional_requirements" className="form-label">
                ข้อกำหนดเพิ่มเติม
              </label>
              <textarea
                id="additional_requirements"
                name="additional_requirements"
                value={formData.additional_requirements}
                onChange={handleInputChange}
                className="form-textarea"
                rows="3"
                placeholder="เช่น สามารถทำงานเป็นทีมได้ดี มีความกระตือรือร้น"
              />
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default CreateJobForm;
