// frontend/src/components/Admin/CompanyManagement.jsx - Fixed Version
import React, { useState, useEffect } from 'react';
import companyService from '../../services/companyService';
import LoadingSpinner from '../LoadingSpinner/LoadingSpinner';

// Import Modal Components
import { CreateCompanyModal, EditCompanyModal } from './CompanyModals';

const CompanyManagement = () => {
  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // Pagination และ Filter states
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [industryFilter, setIndustryFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  
  // Modal states - แก้ไขการจัดการ state
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showHRModal, setShowHRModal] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);

  // Real-time search
  useEffect(() => {
    const delayedSearch = setTimeout(() => {
      setCurrentPage(1);
      loadCompanies(1, searchTerm, industryFilter, statusFilter);
    }, 500);

    return () => clearTimeout(delayedSearch);
  }, [searchTerm, industryFilter, statusFilter]);

  // Load companies on mount
  useEffect(() => {
    loadCompanies(1, '', '', '');
  }, []);

  const loadCompanies = async (page = 1, search = '', industry = '', status = '') => {
    try {
      setLoading(true);
      setError('');
      
      const result = await companyService.getCompanies({
        page,
        limit: 10,
        search: search.trim(),
        industry: industry || null,
        is_active: status === 'active' ? true : status === 'inactive' ? false : null
      });
      
      if (result.success) {
        setCompanies(result.data);
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Companies loading error:', error);
      setError('เกิดข้อผิดพลาดในการโหลดรายการบริษัท');
    } finally {
      setLoading(false);
    }
  };

  // Handle Edit Company - แก้ไขให้แสดง Modal
  const handleEditCompany = async (companyId) => {
    try {
      const result = await companyService.getCompanyById(companyId);
      if (result.success) {
        setSelectedCompany(result.data);
        setShowEditModal(true);
      } else {
        alert('เกิดข้อผิดพลาดในการโหลดข้อมูลบริษัท: ' + result.error);
      }
    } catch (error) {
      console.error('Get company error:', error);
      alert('เกิดข้อผิดพลาดในการโหลดข้อมูลบริษัท');
    }
  };

  // Handle Manage HR - แก้ไขให้แสดง Modal
  const handleManageHR = async (companyId) => {
    try {
      const result = await companyService.getCompanyById(companyId);
      if (result.success) {
        setSelectedCompany(result.data);
        setShowHRModal(true);
      } else {
        alert('เกิดข้อผิดพลาดในการโหลดข้อมูลบริษัท: ' + result.error);
      }
    } catch (error) {
      console.error('Get company error:', error);
      alert('เกิดข้อผิดพลาดในการโหลดข้อมูลบริษัท');
    }
  };

  // Handle Delete Company
  const handleDeleteCompany = async (companyId, companyName) => {
    if (window.confirm(`คุณต้องการลบบริษัท "${companyName}" หรือไม่?`)) {
      try {
        const result = await companyService.deleteCompany(companyId);
        if (result.success) {
          alert('ลบบริษัทสำเร็จ');
          loadCompanies(currentPage, searchTerm, industryFilter, statusFilter);
        } else {
          alert(result.error);
        }
      } catch (error) {
        console.error('Delete company error:', error);
        alert('เกิดข้อผิดพลาดในการลบบริษัท');
      }
    }
  };

  // Handle Modal Success - รีเฟรชข้อมูล
  const handleModalSuccess = () => {
    loadCompanies(currentPage, searchTerm, industryFilter, statusFilter);
    setShowCreateModal(false);
    setShowEditModal(false);
    setShowHRModal(false);
    setSelectedCompany(null);
  };

  // Handle Modal Close
  const handleModalClose = () => {
    setShowCreateModal(false);
    setShowEditModal(false);
    setShowHRModal(false);
    setSelectedCompany(null);
  };

  // Format date
  const formatDate = (dateString) => {
    if (!dateString) return 'ไม่ระบุ';
    return new Date(dateString).toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="company-management">
      <div className="section-header">
        <h2>🏢 จัดการบริษัท</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowCreateModal(true)}
        >
          + เพิ่มบริษัทใหม่
        </button>
      </div>

      {/* Error Display */}
      {error && (
        <div className="error-banner">
          <div className="error-content">
            <span className="error-icon">⚠️</span>
            <span className="error-text">{error}</span>
            <button 
              className="error-close"
              onClick={() => setError('')}
            >
              ×
            </button>
          </div>
        </div>
      )}

      {/* Filters */}
      <div className="filters">
        <div className="filter-group">
          <input
            type="text"
            placeholder="🔍 ค้นหาบริษัท..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-group">
          <select
            value={industryFilter}
            onChange={(e) => setIndustryFilter(e.target.value)}
            className="filter-select"
          >
            <option value="">ประเภทธุรกิจทั้งหมด</option>
            <option value="เทคโนโลยีสารสนเทศ">เทคโนโลยีสารสนเทศ</option>
            <option value="การเงินและธนาคาร">การเงินและธนาคาร</option>
            <option value="การผลิต">การผลิต</option>
            <option value="การค้าปลีก">การค้าปลีก</option>
            <option value="การศึกษา">การศึกษา</option>
            <option value="สุขภาพและการแพทย์">สุขภาพและการแพทย์</option>
            <option value="การก่อสร้าง">การก่อสร้าง</option>
            <option value="การท่องเที่ยว">การท่องเที่ยว</option>
            <option value="ขนส่งและโลจิสติกส์">ขนส่งและโลจิสติกส์</option>
            <option value="พลังงาน">พลังงาน</option>
            <option value="สื่อและบันเทิง">สื่อและบันเทิง</option>
            <option value="อสังหาริมทรัพย์">อสังหาริมทรัพย์</option>
            <option value="อื่นๆ">อื่นๆ</option>
          </select>
        </div>

        <div className="filter-group">
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="filter-select"
          >
            <option value="">สถานะทั้งหมด</option>
            <option value="active">ใช้งานได้</option>
            <option value="inactive">ไม่ใช้งาน</option>
          </select>
        </div>

        <button
          onClick={() => {
            setSearchTerm('');
            setIndustryFilter('');
            setStatusFilter('');
            setCurrentPage(1);
            loadCompanies(1, '', '', '');
          }}
          className="btn btn-secondary"
        >
          ล้างตัวกรอง
        </button>
      </div>

      {/* Companies Table */}
      <div className="table-container">
        {loading ? (
          <LoadingSpinner message="กำลังโหลดรายการบริษัท..." />
        ) : (
          <table className="companies-table">
            <thead>
              <tr>
                <th>บริษัท</th>
                <th>ประเภทธุรกิจ</th>
                <th>ที่ตั้ง</th>
                <th>HR</th>
                <th>สถานะ</th>
                <th>วันที่สร้าง</th>
                <th>การดำเนินการ</th>
              </tr>
            </thead>
            <tbody>
              {companies.length > 0 ? companies.map(company => (
                <tr key={company.id}>
                  <td>
                    <div className="company-info">
                      <div className="company-avatar">
                        {company.name.charAt(0).toUpperCase()}
                      </div>
                      <div className="company-details">
                        <div className="company-name">{company.name}</div>
                        {company.website && (
                          <a 
                            href={company.website} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="company-website"
                          >
                            🌐 เว็บไซต์
                          </a>
                        )}
                      </div>
                    </div>
                  </td>
                  <td>
                    <span className="industry-badge">
                      {company.industry}
                    </span>
                  </td>
                  <td>{company.location || 'ไม่ระบุ'}</td>
                  <td>
                    <div className="hr-count">
                      <span className="hr-active">{company.hr_count || 0}</span>
                      <span className="hr-total">HR</span>
                    </div>
                  </td>
                  <td>
                    <span className={`status-badge ${company.is_active ? 'active' : 'inactive'}`}>
                      {company.is_active ? '✅ ใช้งานได้' : '❌ ไม่ใช้งาน'}
                    </span>
                  </td>
                  <td>{formatDate(company.created_at)}</td>
                  <td>
                    <div className="action-buttons">
                      <button
                        onClick={() => handleManageHR(company.id)}
                        className="action-btn hr"
                        title="จัดการ HR"
                      >
                        👥
                      </button>
                      <button
                        onClick={() => handleEditCompany(company.id)}
                        className="action-btn edit"
                        title="แก้ไข"
                      >
                        ✏️
                      </button>
                      <button
                        onClick={() => handleDeleteCompany(company.id, company.name)}
                        className="action-btn delete"
                        title="ลบ"
                      >
                        🗑️
                      </button>
                    </div>
                  </td>
                </tr>
              )) : (
                <tr>
                  <td colSpan="7" className="no-data">
                    {searchTerm || industryFilter || statusFilter ? 
                      'ไม่พบบริษัทที่ตรงกับเงื่อนไขการค้นหา' : 
                      'ไม่มีข้อมูลบริษัท'}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* Pagination */}
      {companies.length > 0 && (
        <div className="pagination">
          <button 
            className="btn btn-secondary"
            disabled={currentPage === 1}
            onClick={() => {
              const newPage = currentPage - 1;
              setCurrentPage(newPage);
              loadCompanies(newPage, searchTerm, industryFilter, statusFilter);
            }}
          >
            ← ก่อนหน้า
          </button>
          
          <span className="page-info">หน้า {currentPage}</span>
          
          <button 
            className="btn btn-secondary"
            disabled={companies.length < 10}
            onClick={() => {
              const newPage = currentPage + 1;
              setCurrentPage(newPage);
              loadCompanies(newPage, searchTerm, industryFilter, statusFilter);
            }}
          >
            ถัดไป →
          </button>
        </div>
      )}

      {/* Modals */}
      {showCreateModal && (
        <CreateCompanyModal
          onClose={handleModalClose}
          onSuccess={handleModalSuccess}
        />
      )}

      {showEditModal && selectedCompany && (
        <EditCompanyModal
          company={selectedCompany}
          onClose={handleModalClose}
          onSuccess={handleModalSuccess}
        />
      )}

      {showHRModal && selectedCompany && (
        <HRManagementModal
          company={selectedCompany}
          onClose={handleModalClose}
          onSuccess={handleModalSuccess}
        />
      )}
    </div>
  );
};

// HR Management Modal Component
const HRManagementModal = ({ company, onClose, onSuccess }) => {
  const [hrUsers, setHrUsers] = useState([]);
  const [availableHRUsers, setAvailableHRUsers] = useState([]);
  const [selectedHRIds, setSelectedHRIds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isAssigning, setIsAssigning] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadData();
  }, [company.id]);

  const loadData = async () => {
    try {
      setLoading(true);
      setError('');

      // โหลด HR ปัจจุบันของบริษัท
      const hrResult = await companyService.getCompanyHR(company.id);
      if (hrResult.success) {
        setHrUsers(hrResult.data);
      }

      // โหลด HR ที่ใช้ได้ทั้งหมด
      const availableResult = await companyService.getAvailableHR();
      if (availableResult.success) {
        setAvailableHRUsers(availableResult.data);
      }

    } catch (error) {
      console.error('Load HR data error:', error);
      setError('เกิดข้อผิดพลาดในการโหลดข้อมูล HR');
    } finally {
      setLoading(false);
    }
  };

  const handleAssignHR = async () => {
    if (selectedHRIds.length === 0) {
      alert('กรุณาเลือก HR อย่างน้อย 1 คน');
      return;
    }

    try {
      setIsAssigning(true);
      const result = await companyService.assignHRToCompany(company.id, selectedHRIds);
      
      if (result.success) {
        alert(result.message);
        setSelectedHRIds([]);
        loadData();
        onSuccess();
      } else {
        alert(result.error);
      }
    } catch (error) {
      console.error('Assign HR error:', error);
      alert('เกิดข้อผิดพลาดในการเพิ่ม HR');
    } finally {
      setIsAssigning(false);
    }
  };

  const handleRemoveHR = async (userId, username) => {
    if (window.confirm(`คุณต้องการลบ HR "${username}" จากบริษัทนี้หรือไม่?`)) {
      try {
        const result = await companyService.removeHRFromCompany(company.id, userId);
        if (result.success) {
          alert(result.message);
          loadData();
          onSuccess();
        } else {
          alert(result.error);
        }
      } catch (error) {
        console.error('Remove HR error:', error);
        alert('เกิดข้อผิดพลาดในการลบ HR');
      }
    }
  };

  const handleHRSelection = (userId) => {
    setSelectedHRIds(prev => {
      if (prev.includes(userId)) {
        return prev.filter(id => id !== userId);
      } else {
        return [...prev, userId];
      }
    });
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'ไม่เคยเข้าสู่ระบบ';
    return new Date(dateString).toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // กรอง HR ที่ยังไม่ได้ assign
  const unassignedHRUsers = availableHRUsers.filter(user => 
    !hrUsers.some(hr => hr.id === user.id)
  );

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal hr-management-modal large-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>จัดการ HR: {company.name}</h3>
          <button className="modal-close" onClick={onClose}>×</button>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <div className="hr-management-content">
          {loading ? (
            <LoadingSpinner message="กำลังโหลดข้อมูล HR..." />
          ) : (
            <div className="hr-sections">
              {/* Current HR Users */}
              <div className="hr-section">
                <h4>HR ปัจจุบัน ({hrUsers.length} คน)</h4>
                <div className="hr-list current-hr">
                  {hrUsers.length > 0 ? hrUsers.map(hr => (
                    <div key={hr.id} className="hr-item">
                      <div className="hr-info">
                        <div className="hr-avatar">
                          {hr.full_name ? hr.full_name.charAt(0).toUpperCase() : hr.username.charAt(0).toUpperCase()}
                        </div>
                        <div className="hr-details">
                          <div className="hr-name">{hr.full_name || hr.username}</div>
                          <div className="hr-email">{hr.email}</div>
                          <div className="hr-last-login">เข้าสู่ระบบล่าสุด: {formatDate(hr.last_login)}</div>
                        </div>
                      </div>
                      <button
                        onClick={() => handleRemoveHR(hr.id, hr.username)}
                        className="btn btn-danger btn-sm"
                      >
                        ลบออก
                      </button>
                    </div>
                  )) : (
                    <div className="no-data">ยังไม่มี HR ในบริษัทนี้</div>
                  )}
                </div>
              </div>

              {/* Available HR Users */}
              <div className="hr-section">
                <h4>HR ที่สามารถเพิ่มได้ ({unassignedHRUsers.length} คน)</h4>
                <div className="hr-list available-hr">
                  {unassignedHRUsers.length > 0 ? unassignedHRUsers.map(hr => (
                    <div key={hr.id} className="hr-item selectable">
                      <label className="hr-checkbox">
                        <input
                          type="checkbox"
                          checked={selectedHRIds.includes(hr.id)}
                          onChange={() => handleHRSelection(hr.id)}
                        />
                        <div className="hr-info">
                          <div className="hr-avatar">
                            {hr.full_name ? hr.full_name.charAt(0).toUpperCase() : hr.username.charAt(0).toUpperCase()}
                          </div>
                          <div className="hr-details">
                            <div className="hr-name">{hr.full_name || hr.username}</div>
                            <div className="hr-email">{hr.email}</div>
                            <div className="hr-last-login">เข้าสู่ระบบล่าสุด: {formatDate(hr.last_login)}</div>
                          </div>
                        </div>
                      </label>
                    </div>
                  )) : (
                    <div className="no-data">ไม่มี HR ที่สามารถเพิ่มได้</div>
                  )}
                </div>

                {selectedHRIds.length > 0 && (
                  <div className="assign-actions">
                    <button
                      onClick={handleAssignHR}
                      className="btn btn-primary"
                      disabled={isAssigning}
                    >
                      {isAssigning ? 'กำลังเพิ่ม...' : `เพิ่ม HR (${selectedHRIds.length} คน)`}
                    </button>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        <div className="modal-actions">
          <button
            type="button"
            onClick={onClose}
            className="btn btn-secondary"
          >
            ปิด
          </button>
        </div>
      </div>
    </div>
  );
};

export default CompanyManagement;
