// frontend/src/contexts/ProfileContext.js - Global Profile Management
import React, { createContext, useContext, useState, useEffect } from 'react';
import { useAuth } from './AuthContext';

const ProfileContext = createContext();

export const useProfile = () => {
  const context = useContext(ProfileContext);
  if (!context) {
    throw new Error('useProfile must be used within a ProfileProvider');
  }
  return context;
};

export const ProfileProvider = ({ children }) => {
  const { user, isAuthenticated } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ฟังก์ชันดึงข้อมูล Profile
  const fetchProfile = async () => {
    if (!isAuthenticated() || !user) {
      setLoading(false);
      setProfile(null);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('access_token');
      const response = await fetch('http://localhost:8000/api/auth/profile/', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const profileData = await response.json();
        setProfile(profileData);
      } else {
        throw new Error('ไม่สามารถดึงข้อมูลโปรไฟล์ได้');
      }
    } catch (err) {
      console.error('Error fetching profile:', err);
      setError(err.message);
      setProfile(null);
    } finally {
      setLoading(false);
    }
  };

  // ฟังก์ชันอัพเดต Profile
  const updateProfile = async (formData) => {
    if (!isAuthenticated()) {
      throw new Error('กรุณาเข้าสู่ระบบ');
    }

    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch('http://localhost:8000/api/auth/profile/', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });

      if (response.ok) {
        const updatedProfile = await response.json();
        setProfile(updatedProfile);
        return { success: true, data: updatedProfile };
      } else {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'ไม่สามารถอัพเดตโปรไฟล์ได้');
      }
    } catch (err) {
      console.error('Error updating profile:', err);
      throw err;
    }
  };

  // ฟังก์ชันอัพโหลดรูปโปรไฟล์
  const uploadProfileImage = async (imageFile) => {
    if (!isAuthenticated()) {
      throw new Error('กรุณาเข้าสู่ระบบ');
    }

    try {
      const formData = new FormData();
      formData.append('profile_image', imageFile);

      const token = localStorage.getItem('access_token');
      const response = await fetch('http://localhost:8000/api/auth/upload-profile-image/', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        
        // อัพเดต profile ใน state ทันที
        setProfile(prev => ({
          ...prev,
          profile_image: result.profile_image
        }));
        
        return { success: true, data: result };
      } else {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'ไม่สามารถอัพโหลดรูปภาพได้');
      }
    } catch (err) {
      console.error('Error uploading profile image:', err);
      throw err;
    }
  };

  // Helper functions
  const getInitials = (fullName) => {
    if (!fullName) return 'U';
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return (names[0][0] + names[1][0]).toUpperCase();
    }
    return names[0][0].toUpperCase();
  };

  const getDisplayName = (fullName) => {
    if (!fullName) return 'User';
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return names[0];
    }
    return fullName;
  };

  const getProfileImageUrl = () => {
    if (!profile?.profile_image) return null;
    
    if (profile.profile_image.startsWith('http')) {
      return profile.profile_image;
    }
    
    return `http://localhost:8000${profile.profile_image}`;
  };

  // เรียกดึงข้อมูลเมื่อ user เปลี่ยน
  useEffect(() => {
    fetchProfile();
  }, [user, isAuthenticated]);

  // ล้างข้อมูลเมื่อ logout
  useEffect(() => {
    if (!isAuthenticated()) {
      setProfile(null);
      setError(null);
      setLoading(false);
    }
  }, [isAuthenticated]);

  const value = {
    // State
    profile,
    loading,
    error,
    
    // Actions
    updateProfile,
    uploadProfileImage,
    refreshProfile: fetchProfile,
    
    // Helper functions
    getInitials,
    getDisplayName,
    getProfileImageUrl,
    
    // Computed values
    fullName: profile?.full_name || user?.full_name || '',
    email: profile?.email || user?.email || '',
    userType: profile?.user_type || user?.user_type || '',
    initials: getInitials(profile?.full_name || user?.full_name),
    displayName: getDisplayName(profile?.full_name || user?.full_name),
    profileImageUrl: getProfileImageUrl(),
  };

  return (
    <ProfileContext.Provider value={value}>
      {children}
    </ProfileContext.Provider>
  );
};
