import React, { useState } from 'react';
import Navbar from './components/Navbar/Navbar';
import Homepage from './components/Homepage/Homepage';
import Login from './components/Login/Login';
import Register from './components/Register/Register';
import './styles/global.css';

function App() {
  // สำหรับทดสอบเปลี่ยนหน้า (ในอนาคตจะใช้ React Router)
  const [currentPage, setCurrentPage] = useState('home');

  const renderPage = () => {
    switch (currentPage) {
      case 'login':
        return <Login />;
      case 'register':
        return <Register />;
      default:
        return <Homepage />;
    }
  };

  return (
    <div className="App">
      <Navbar />
      {renderPage()}

      {/* ปุ่มทดสอบ - ลบทิ้งได้เมื่อใช้ Router */}
      <div style={{ 
        position: 'fixed', 
        bottom: '20px', 
        right: '20px', 
        display: 'flex', 
        gap: '10px',
        zIndex: 1000
      }}>
        <button 
          onClick={() => setCurrentPage('home')}
          style={{ padding: '10px', borderRadius: '5px', border: 'none', background: '#4F46E5', color: 'white' }}
        >
          Home
        </button>
        <button 
          onClick={() => setCurrentPage('login')}
          style={{ padding: '10px', borderRadius: '5px', border: 'none', background: '#4F46E5', color: 'white' }}
        >
          Login
        </button>
        <button 
          onClick={() => setCurrentPage('register')}
          style={{ padding: '10px', borderRadius: '5px', border: 'none', background: '#10B981', color: 'white' }}
        >
          Register
        </button>
      </div>
    </div>
  );
}

export default App;
