// frontend/src/components/HR/JobManagement.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/JobManagement.css';

const JobManagement = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [filter, setFilter] = useState('all');
  const navigate = useNavigate();

  // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/jobs', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏´‡∏£‡∏∑‡∏≠‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ API ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• mock
        console.log('API not available, using mock data');
        setJobs([]); // ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô array ‡∏ß‡πà‡∏≤‡∏á
        return;
      }

      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        const data = await response.json();
        setJobs(data.jobs || []);
      } else {
        // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà JSON ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• mock
        console.log('Non-JSON response, using mock data');
        setJobs([]);
      }
    } catch (err) {
      console.error('Error fetching jobs:', err);
      // ‡πÉ‡∏ä‡πâ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• mock ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î
      setJobs([]);
    } finally {
      setLoading(false);
    }
  };

  // ‡∏Å‡∏£‡∏≠‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏≤‡∏°‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞
  const filteredJobs = jobs.filter(job => {
    if (filter === 'all') return true;
    if (filter === 'active') return job.is_active;
    if (filter === 'inactive') return !job.is_active;
    return true;
  });

  // ‡∏™‡∏ñ‡∏¥‡∏ï‡∏¥‡∏î‡πà‡∏ß‡∏ô
  const stats = {
    total: jobs.length,
    active: jobs.filter(job => job.is_active).length,
    inactive: jobs.filter(job => !job.is_active).length,
    applications: jobs.reduce((sum, job) => sum + (job.applications_count || 0), 0)
  };

  const handleCreateJob = () => {
    navigate('/hr/jobs/create');
  };

  const handleEditJob = (jobId) => {
    navigate(`/hr/jobs/edit/${jobId}`);
  };

  const handleViewApplications = (jobId) => {
    navigate(`/hr/jobs/${jobId}/applications`);
  };

  const toggleJobStatus = async (jobId, currentStatus) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/jobs/${jobId}/toggle-status`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        await fetchJobs(); // ‡∏£‡∏µ‡πÄ‡∏ü‡∏£‡∏ä‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
      }
    } catch (err) {
      console.error('Error toggling job status:', err);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">
          <h3>‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î</h3>
          <p>{error}</p>
          <button onClick={fetchJobs} className="retry-button">
            ‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="job-management">
      <div className="page-header">
        <div className="header-left">
          <h1>‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô</h1>
          <p>‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡πÅ‡∏•‡∏∞‡∏ï‡∏¥‡∏î‡∏ï‡∏≤‡∏°‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</p>
        </div>
        <div className="header-actions">
          <button 
            onClick={handleCreateJob}
            className="primary-button"
          >
            <span className="icon">‚ûï</span>
            ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÉ‡∏´‡∏°‡πà
          </button>
        </div>
      </div>

      {/* ‡∏™‡∏ñ‡∏¥‡∏ï‡∏¥‡∏î‡πà‡∏ß‡∏ô */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìä</div>
          <div className="stat-content">
            <div className="stat-number">{stats.total}</div>
            <div className="stat-label">‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üü¢</div>
          <div className="stat-content">
            <div className="stat-number">{stats.active}</div>
            <div className="stat-label">‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚è∏Ô∏è</div>
          <div className="stat-content">
            <div className="stat-number">{stats.inactive}</div>
            <div className="stat-label">‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-content">
            <div className="stat-number">{stats.applications}</div>
            <div className="stat-label">‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</div>
          </div>
        </div>
      </div>

      {/* ‡∏ü‡∏¥‡∏•‡πÄ‡∏ï‡∏≠‡∏£‡πå */}
      <div className="filters-section">
        <div className="filter-tabs">
          <button 
            className={filter === 'all' ? 'active' : ''}
            onClick={() => setFilter('all')}
          >
            ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î ({stats.total})
          </button>
          <button 
            className={filter === 'active' ? 'active' : ''}
            onClick={() => setFilter('active')}
          >
            ‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£ ({stats.active})
          </button>
          <button 
            className={filter === 'inactive' ? 'active' : ''}
            onClick={() => setFilter('inactive')}
          >
            ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£ ({stats.inactive})
          </button>
        </div>
      </div>

      {/* ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô */}
      <div className="jobs-list">
        {filteredJobs.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üìù</div>
            <h3>‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô</h3>
            <p>‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÇ‡∏î‡∏¢‡∏Å‡∏≤‡∏£‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô‡πÅ‡∏£‡∏Å‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì</p>
            <button 
              onClick={handleCreateJob}
              className="primary-button"
            >
              ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÉ‡∏´‡∏°‡πà
            </button>
          </div>
        ) : (
          filteredJobs.map(job => (
            <div key={job.id} className="job-card">
              <div className="job-header">
                <div className="job-title-section">
                  <h3 className="job-title">{job.title}</h3>
                  <div className="job-meta">
                    <span className="job-code">{job.job_code}</span>
                    <span className="job-department">{job.department}</span>
                    <span className={`job-status ${job.is_active ? 'active' : 'inactive'}`}>
                      {job.is_active ? '‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£' : '‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£'}
                    </span>
                  </div>
                </div>
                <div className="job-actions">
                  <button 
                    onClick={() => handleViewApplications(job.id)}
                    className="secondary-button"
                  >
                    ‡∏î‡∏π‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£ ({job.applications_count || 0})
                  </button>
                  <button 
                    onClick={() => handleEditJob(job.id)}
                    className="secondary-button"
                  >
                    ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç
                  </button>
                  <button 
                    onClick={() => toggleJobStatus(job.id, job.is_active)}
                    className={`toggle-button ${job.is_active ? 'active' : 'inactive'}`}
                  >
                    {job.is_active ? '‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£' : '‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£'}
                  </button>
                </div>
              </div>

              <div className="job-details">
                <div className="job-description">
                  <p>{job.description}</p>
                </div>

                <div className="job-info-grid">
                  <div className="info-item">
                    <span className="info-label">‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó:</span>
                    <span className="info-value">{job.internship_type}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">‡∏£‡∏∞‡∏¢‡∏∞‡πÄ‡∏ß‡∏•‡∏≤:</span>
                    <span className="info-value">{job.duration_months} ‡πÄ‡∏î‡∏∑‡∏≠‡∏ô</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏£‡∏±‡∏ö:</span>
                    <span className="info-value">{job.positions_available} ‡∏Ñ‡∏ô</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">‡∏Ñ‡πà‡∏≤‡∏ï‡∏≠‡∏ö‡πÅ‡∏ó‡∏ô:</span>
                    <span className="info-value">{job.compensation_type}</span>
                  </div>
                </div>

                {job.required_skills && job.required_skills.length > 0 && (
                  <div className="skills-section">
                    <span className="skills-label">‡∏ó‡∏±‡∏Å‡∏©‡∏∞‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£:</span>
                    <div className="skills-tags">
                      {job.required_skills.map((skill, index) => (
                        <span key={index} className="skill-tag">
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="job-footer">
                <div className="job-stats">
                  <div className="stat-item">
                    <span className="stat-value">{job.applications_count || 0}</span>
                    <span className="stat-label">‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</span>
                  </div>
