// frontend/src/components/HR/HRDashboard.jsx (‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï)
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './HRDashboard.css';

const HRDashboard = () => {
  const [companyInfo, setCompanyInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [jobStats, setJobStats] = useState({
    total: 0,
    active: 0,
    applications: 0,
    qualified: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');
      
      // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡πÅ‡∏•‡∏∞‡∏™‡∏ñ‡∏¥‡∏ï‡∏¥
      const [companyResponse, jobStatsResponse] = await Promise.all([
        fetch('/api/profile/company-info', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }),
        fetch('/api/jobs/statistics/overview', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        })
      ]);

      if (companyResponse.ok) {
        const companyData = await companyResponse.json();
        setCompanyInfo(companyData);
      }

      if (jobStatsResponse.ok) {
        const statsData = await jobStatsResponse.json();
        setJobStats({
          total: statsData.total_internships || 0,
          active: statsData.active_internships || 0,
          applications: statsData.total_applications || 0,
          qualified: statsData.qualified_applications || 0
        });
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleNavigateToJobs = () => {
    navigate('/hr/jobs');
  };

  const handleCreateJob = () => {
    navigate('/hr/jobs/create');
  };

  const handleViewApplications = () => {
    navigate('/hr/applications');
  };

  const handleViewCompanies = () => {
    navigate('/hr/companies');
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•...</p>
      </div>
    );
  }

  return (
    <div className="hr-dashboard">
      <div className="dashboard-container">
        {/* Welcome Section */}
        <div className="welcome-section">
          <div className="welcome-content">
            <h1>‡∏¢‡∏¥‡∏ô‡∏î‡∏µ‡∏ï‡πâ‡∏≠‡∏ô‡∏£‡∏±‡∏ö HR Manager</h1>
            <p>‡∏£‡∏∞‡∏ö‡∏ö‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö HR</p>
            {companyInfo && companyInfo.company && (
              <div className="company-info">
                <div className="company-badge">
                  <span className="company-logo">üè¢</span>
                  <div className="company-details">
                    <h3>{companyInfo.company.name}</h3>
                    <p>{companyInfo.company.industry}</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Quick Stats */}
        {companyInfo && companyInfo.company && (
          <div className="stats-section">
            <div className="stats-grid">
              <div className="stat-card" onClick={handleNavigateToJobs}>
                <div className="stat-icon">üìä</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.total}</div>
                  <div className="stat-label">‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</div>
                </div>
                <div className="stat-arrow">‚Üí</div>
              </div>
              
              <div className="stat-card" onClick={handleNavigateToJobs}>
                <div className="stat-icon">üü¢</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.active}</div>
                  <div className="stat-label">‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</div>
                </div>
                <div className="stat-arrow">‚Üí</div>
              </div>
              
              <div className="stat-card" onClick={handleViewApplications}>
                <div className="stat-icon">üìù</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.applications}</div>
                  <div className="stat-label">‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</div>
                </div>
                <div className="stat-arrow">‚Üí</div>
              </div>
              
              <div className="stat-card" onClick={handleViewApplications}>
                <div className="stat-icon">‚úÖ</div>
                <div className="stat-content">
                  <div className="stat-number">{jobStats.qualified}</div>
                  <div className="stat-label">‡∏ú‡πà‡∏≤‡∏ô‡∏Ñ‡∏∏‡∏ì‡∏™‡∏°‡∏ö‡∏±‡∏ï‡∏¥</div>
                </div>
                <div className="stat-arrow">‚Üí</div>
              </div>
            </div>
          </div>
        )}

        {/* Quick Actions */}
        {companyInfo && companyInfo.company && (
          <div className="actions-section">
            <h3>‡∏ü‡∏µ‡πÄ‡∏à‡∏≠‡∏£‡πå‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡πÑ‡∏î‡πâ</h3>
            <div className="actions-grid">
              <div className="action-card primary" onClick={handleCreateJob}>
                <div className="action-icon">‚ûï</div>
                <div className="action-content">
                  <h4>‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô‡πÉ‡∏´‡∏°‡πà</h4>
                  <p>‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô‡πÉ‡∏´‡∏°‡πà</p>
                </div>
              </div>
              
              <div className="action-card" onClick={handleNavigateToJobs}>
                <div className="action-icon">üìã</div>
                <div className="action-content">
                  <h4>‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô</h4>
                  <p>‡∏î‡∏π‡πÅ‡∏•‡∏∞‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà</p>
                </div>
              </div>
              
              <div className="action-card" onClick={handleViewApplications}>
                <div className="action-icon">üë•</div>
                <div className="action-content">
                  <h4>‡∏î‡∏π‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</h4>
                  <p>‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡πÅ‡∏•‡∏∞‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô‡∏ú‡∏π‡πâ‡∏™‡∏°‡∏±‡∏Ñ‡∏£</p>
                </div>
              </div>
              
              <div className="action-card" onClick={handleViewCompanies}>
                <div className="action-icon">üè¢</div>
                <div className="action-content">
                  <h4>‡∏î‡∏π‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏≠‡∏∑‡πà‡∏ô</h4>
                  <p>‡πÄ‡∏õ‡∏£‡∏µ‡∏¢‡∏ö‡πÄ‡∏ó‡∏µ‡∏¢‡∏ö‡∏Å‡∏±‡∏ö‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏Ñ‡∏π‡πà‡πÅ‡∏Ç‡πà‡∏á</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Getting Started - ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö HR ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó */}
        {companyInfo && companyInfo.user_type === 'HR' && !companyInfo.company && (
          <div className="getting-started">
            <div className="getting-started-content">
              <div className="getting-started-icon">üöÄ</div>
              <h3>‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô</h3>
              <p>‡∏Ñ‡∏∏‡∏ì‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡∏£‡∏±‡∏ö‡∏Å‡∏≤‡∏£‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡πÄ‡∏õ‡πá‡∏ô HR ‡∏Ç‡∏≠‡∏á‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡πÉ‡∏î</p>
              
              <div className="steps-container">
                <div className="step">
                  <div className="step-number">1</div>
                  <div className="step-content">
                    <h4>‡∏£‡∏≠‡∏Å‡∏≤‡∏£‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó</h4>
                    <p>Admin ‡∏à‡∏∞‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡πÉ‡∏´‡πâ‡∏Ñ‡∏∏‡∏ì‡πÄ‡∏õ‡πá‡∏ô HR ‡∏Ç‡∏≠‡∏á‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡πÉ‡∏î‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏´‡∏ô‡∏∂‡πà‡∏á</p>
