// frontend/src/components/Profile/Settings.jsx
import React, { useState } from 'react';

const Settings = ({ profileData, profileService }) => {
  const [settings, setSettings] = useState({
    notifications: true,
    emails: false,
    privacy: true,
    autoUpdate: false
  });
  const [loading, setLoading] = useState(false);

  const handleToggle = (settingName) => {
    setSettings(prev => ({
      ...prev,
      [settingName]: !prev[settingName]
    }));
  };

  const handleSave = async () => {
    setLoading(true);
    try {
      // บันทึกการตั้งค่า
      await new Promise(resolve => setTimeout(resolve, 1000)); // จำลองการบันทึก
      alert('บันทึกการตั้งค่าเรียบร้อยแล้ว');
    } catch (error) {
      alert('เกิดข้อผิดพลาดในการบันทึกการตั้งค่า');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="settings-section">
      <div className="profile-section-header">
        <h2>ตั้งค่า</h2>
      </div>

      <div className="settings-grid">
        <div className="setting-item">
          <div className="setting-info">
            <div className="setting-icon">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path d="M8 2v4l-6 6v10h20V12l-6-6V2z"/>
                <path d="M8 2v4l6 0V2"/>
              </svg>
            </div>
            <div className="setting-content">
              <h3>การแจ้งเตือน</h3>
              <p>เปิดการแจ้งเตือนในระบบ</p>
            </div>
          </div>
          <div className="setting-toggle">
            <label className="toggle-switch">
              <input
                type="checkbox"
                checked={settings.notifications}
                onChange={() => handleToggle('notifications')}
              />
              <span className="toggle-slider"></span>
            </label>
          </div>
        </div>

        <div className="setting-item">
          <div className="setting-info">
            <div className="setting-icon">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"/>
                <polyline points="22,6 12,13 2,6"/>
              </svg>
            </div>
            <div className="setting-content">
              <h3>ในแอปทำ</h3>
              <p>แจ้งเตือนผ่านอีเมล</p>
            </div>
          </div>
          <div className="setting-toggle">
            <label className="toggle-switch">
              <input
                type="checkbox"
                checked={settings.emails}
                onChange={() => handleToggle('emails')}
              />
              <span className="toggle-slider"></span>
            </label>
          </div>
        </div>

        <div className="setting-item">
          <div className="setting-info">
            <div className="setting-icon">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <circle cx="12" cy="12" r="3"/>
                <path d="M19.4 15a1.65 1.65 0 0 0 .33 1.82l.06.06a2 2 0 0 1 0 2.83 2 2 0 0 1-2.83 0l-.06-.06a1.65 1.65 0 0 0-1.82-.33 1.65 1.65 0 0 0-1 1.51V21a2 2 0 0 1-2 2 2 2 0 0 1-2-2v-.09A1.65 1.65 0 0 0 9 19.4a1.65 1.65 0 0 0-1.82.33l-.06.06a2 2 0 0 1-2.83 0 2 2 0 0 1 0-2.83l.06-.06a1.65 1.65 0 0 0 .33-1.82 1.65 1.65 0 0 0-1.51-1H3a2 2 0 0 1-2-2 2 2 0 0 1 2-2h.09A1.65 1.65 0 0 0 4.6 9a1.65 1.65 0 0 0-.33-1.82l-.06-.06a2 2 0 0 1 0-2.83 2 2 0 0 1 2.83 0l.06.06a1.65 1.65 0 0 0 1.82.33H9a1.65 1.65 0 0 0 1 1.51V3a2 2 0 0 1 2-2 2 2 0 0 1 2 2v.09a1.65 1.65 0 0 0 1 1.51 1.65 1.65 0 0 0 1.82-.33l.06-.06a2 2 0 0 1 2.83 0 2 2 0 0 1 0 2.83l-.06.06a1.65 1.65 0 0 0-.33 1.82V9a1.65 1.65 0 0 0 1.51 1H21a2 2 0 0 1 2 2 2 2 0 0 1-2 2h-.09a1.65 1.65 0 0 0-1.51 1z"/>
              </svg>
            </div>
            <div className="setting-content">
              <h3>ความเป็นส่วนตัว</h3>
              <p>ระบุการมองเห็นโปรไฟล์</p>
            </div>
          </div>
          <div className="setting-toggle">
            <label className="toggle-switch">
              <input
                type="checkbox"
                checked={settings.privacy}
                onChange={() => handleToggle('privacy')}
              />
              <span className="toggle-slider"></span>
            </label>
          </div>
        </div>

        <div className="setting-item">
          <div className="setting-info">
            <div className="setting-icon">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path d="M3 11l19-9-9 19-2-8-8-2z"/>
              </svg>
            </div>
            <div className="setting-content">
              <h3>อัปเดตอัตโนมัติ</h3>
              <p>อัปเดตระบบอัตโนมัติ</p>
            </div>
          </div>
          <div className="setting-toggle">
            <button 
              className="save-settings-btn"
              onClick={handleSave}
              disabled={loading}
            >
              ตั้งค่า
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
