import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import '../../styles/navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const { user, logout: authLogout, isAuthenticated } = useAuth();
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isLoggingOut, setIsLoggingOut] = useState(false); // ✅ เพิ่มเพื่อป้องกัน double click
  const dropdownRef = useRef(null);
  const isMountedRef = useRef(true); // ✅ เพิ่มเพื่อตรวจสอบ component mount

  // ตั้งค่า mounted state
  useEffect(() => {
    isMountedRef.current = true;
    return () => {
      isMountedRef.current = false;
    };
  }, []);

  // ปิด dropdown เมื่อคลิกข้างนอก
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // ✅ ฟังก์ชัน logout (แก้ไขเฉพาะปัญหา Error)
  const handleLogout = async () => {
    // ป้องกัน multiple logout calls
    if (isLoggingOut || !isMountedRef.current) {
      return;
    }

    try {
      setIsLoggingOut(true);
      setIsDropdownOpen(false);
      
      const result = await authLogout();
      
      if (result.success) {
        alert('ออกจากระบบเรียบร้อยแล้ว');
        
        // ใช้ setTimeout เล็กน้อยเพื่อให้ alert แสดงก่อน navigate
        setTimeout(() => {
          if (isMountedRef.current) {
            navigate('/', { replace: true });
          }
        }, 100);
      }
    } catch (error) {
      console.error('Logout error:', error);
      if (isMountedRef.current) {
        alert('เกิดข้อผิดพลาดในการออกจากระบบ');
      }
    } finally {
      // รีเซ็ต logout state หลังจาก delay เล็กน้อย
      setTimeout(() => {
        if (isMountedRef.current) {
          setIsLoggingOut(false);
        }
      }, 1000);
    }
  };

  // ฟังก์ชันไปหน้า profile
  const handleProfile = () => {
    if (!isMountedRef.current) return;
    setIsDropdownOpen(false);
    navigate('/profile');
  };

  // ⭐ ฟังก์ชันไปหน้า Admin Dashboard
  const handleAdminDashboard = () => {
    if (!isMountedRef.current) return;
    setIsDropdownOpen(false);
    navigate('/admin/dashboard');
  };

  // ⭐ ฟังก์ชันไปหน้า HR Dashboard
  const handleHRDashboard = () => {
    if (!isMountedRef.current) return;
    setIsDropdownOpen(false);
    navigate('/hr/dashboard');
  };

  // ฟังก์ชันสำหรับแสดงชื่อย่อ
  const getInitials = (fullName) => {
    if (!fullName) return 'U';
    
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return (names[0][0] + names[1][0]).toUpperCase();
    }
    return names[0][0].toUpperCase();
  };

  // ฟังก์ชันแสดงชื่อสั้น
  const getDisplayName = (fullName) => {
    if (!fullName) return 'ผู้ใช้';
    
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return `${names[0]} ${names[1].charAt(0)}.`;
    }
    return names[0];
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-logo" onClick={() => navigate('/')}>
          InternScreen
        </div>
        
        <div className="navbar-menu">
          <div className="navbar-links">
            <a href="/" className="navbar-link">หน้าหลัก</a>
            <a href="/jobs" className="navbar-link">งาน</a>
            <a href="/about" className="navbar-link">เกี่ยวกับเรา</a>
          </div>
          
          {isAuthenticated() && user ? (
            <div className="user-section" ref={dropdownRef}>
              <button 
                className="user-button"
                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                disabled={isLoggingOut} // ✅ ป้องกันการคลิกเมื่อกำลัง logout
              >
                <div className="user-avatar">
                  {getInitials(user.full_name)}
                </div>
                <span className="user-name">
                  {getDisplayName(user.full_name)}
                </span>
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <polyline points="6,9 12,15 18,9"></polyline>
                </svg>
              </button>

              {isDropdownOpen && !isLoggingOut && (
                <div className="dropdown-menu">
                  <div className="dropdown-header">
                    <div className="dropdown-user-info">
                      <strong>{user.full_name}</strong>
                      <span className="user-type">{user.user_type}</span>
                    </div>
                  </div>
                  
                  <div className="dropdown-divider"></div>
                  
                  <button className="dropdown-item" onClick={handleProfile}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                      <circle cx="12" cy="7" r="4"></circle>
                    </svg>
                    ข้อมูลส่วนตัว
                  </button>

                  {(user.user_type === 'Admin') && (
                    <button className="dropdown-item admin-item" onClick={handleAdminDashboard}>
                      <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                        <rect x="2" y="3" width="20" height="14" rx="2" ry="2"></rect>
                        <line x1="8" y1="21" x2="16" y2="21"></line>
                        <line x1="12" y1="17" x2="12" y2="21"></line>
                      </svg>
                      Admin Dashboard
                    </button>
                  )}

                  {(user.user_type === 'HR' || user.user_type === 'Admin') && (
                    <button className="dropdown-item hr-item" onClick={handleHRDashboard}>
                      <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                        <path d="M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                        <circle cx="8.5" cy="7" r="4"></circle>
                        <line x1="20" y1="8" x2="20" y2="14"></line>
                        <line x1="23" y1="11" x2="17" y2="11"></line>
                      </svg>
                      HR Dashboard
                    </button>
                  )}
                  
                  <div className="dropdown-divider"></div>
                  
                  <button 
                    className="dropdown-item logout" 
                    onClick={handleLogout}
                    disabled={isLoggingOut}
                  >
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
                      <polyline points="16,17 21,12 16,7"></polyline>
                      <line x1="21" y1="12" x2="9" y2="12"></line>
                    </svg>
                    {isLoggingOut ? 'กำลังออกจากระบบ...' : 'ออกจากระบบ'}
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="auth-buttons">
              <button 
                className="btn-login"
                onClick={() => navigate('/login')}
              >
                เข้าสู่ระบบ
              </button>
              <button 
                className="btn-register"
                onClick={() => navigate('/register')}
              >
                สมัครสมาชิก
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
