// frontend/src/components/Admin/CompanyModals.jsx - Complete Version
import React, { useState } from 'react';
import companyService from '../../services/companyService';

// Create Company Modal Component
export const CreateCompanyModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    name: '',
    industry: '',
    description: '',
    location: '',
    website: '',
    contact_email: '',
    contact_phone: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const industries = [
    'เทคโนโลยีสารสนเทศ',
    'การเงินและธนาคาร',
    'การผลิต',
    'การค้าปลีก',
    'การศึกษา',
    'สุขภาพและการแพทย์',
    'การก่อสร้าง',
    'การท่องเที่ยว',
    'ขนส่งและโลจิสติกส์',
    'พลังงาน',
    'สื่อและบันเทิง',
    'อสังหาริมทรัพย์',
    'อื่นๆ'
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await companyService.createCompany({
        name: formData.name.trim(),
        industry: formData.industry,
        description: formData.description.trim() || null,
        location: formData.location.trim() || null,
        website: formData.website.trim() || null,
        contact_email: formData.contact_email.trim() || null,
        contact_phone: formData.contact_phone.trim() || null
      });

      if (result.success) {
        alert('สร้างบริษัทสำเร็จ');
        onSuccess();
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Create company error:', error);
      setError('เกิดข้อผิดพลาดในการสร้างบริษัท');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal create-company-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>เพิ่มบริษัทใหม่</h3>
          <button className="modal-close" onClick={onClose}>×</button>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="create-company-form">
          <div className="form-group">
            <label htmlFor="name">ชื่อบริษัท *</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              disabled={loading}
              className="form-input"
              placeholder="ชื่อบริษัท จำกัด"
            />
          </div>

          <div className="form-group">
            <label htmlFor="industry">ประเภทธุรกิจ *</label>
            <select
              id="industry"
              name="industry"
              value={formData.industry}
              onChange={handleChange}
              required
              disabled={loading}
              className="form-select"
            >
              <option value="">เลือกประเภทธุรกิจ</option>
              {industries.map(industry => (
                <option key={industry} value={industry}>
                  {industry}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="description">รายละเอียดบริษัท</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              disabled={loading}
              className="form-textarea"
              rows="3"
              placeholder="รายละเอียดเกี่ยวกับบริษัท..."
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="location">ที่ตั้ง</label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="กรุงเทพมหานคร"
              />
            </div>

            <div className="form-group">
              <label htmlFor="website">เว็บไซต์</label>
              <input
                type="url"
                id="website"
                name="website"
                value={formData.website}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="https://example.com"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="contact_email">อีเมลติดต่อ</label>
              <input
                type="email"
                id="contact_email"
                name="contact_email"
                value={formData.contact_email}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="contact@example.com"
              />
            </div>

            <div className="form-group">
              <label htmlFor="contact_phone">เบอร์โทรติดต่อ</label>
              <input
                type="tel"
                id="contact_phone"
                name="contact_phone"
                value={formData.contact_phone}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="02-xxx-xxxx"
              />
            </div>
          </div>

          <div className="modal-actions">
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary"
              disabled={loading}
            >
              ยกเลิก
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'กำลังสร้าง...' : 'สร้างบริษัท'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Edit Company Modal Component
export const EditCompanyModal = ({ company, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    name: company.name || '',
    industry: company.industry || '',
    description: company.description || '',
    location: company.location || '',
    website: company.website || '',
    contact_email: company.contact_email || '',
    contact_phone: company.contact_phone || '',
    is_active: company.is_active !== undefined ? company.is_active : true
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const industries = [
    'เทคโนโลยีสารสนเทศ',
    'การเงินและธนาคาร',
    'การผลิต',
    'การค้าปลีก',
    'การศึกษา',
    'สุขภาพและการแพทย์',
    'การก่อสร้าง',
    'การท่องเที่ยว',
    'ขนส่งและโลจิสติกส์',
    'พลังงาน',
    'สื่อและบันเทิง',
    'อสังหาริมทรัพย์',
    'อื่นๆ'
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await companyService.updateCompany(company.id, {
        name: formData.name.trim(),
        industry: formData.industry,
        description: formData.description.trim() || null,
        location: formData.location.trim() || null,
        website: formData.website.trim() || null,
        contact_email: formData.contact_email.trim() || null,
        contact_phone: formData.contact_phone.trim() || null,
        is_active: formData.is_active
      });

      if (result.success) {
        alert('อัปเดตข้อมูลบริษัทสำเร็จ');
        onSuccess();
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Update company error:', error);
      setError('เกิดข้อผิดพลาดในการอัปเดตข้อมูล');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    if (error) setError('');
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal edit-company-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>แก้ไขข้อมูลบริษัท: {company.name}</h3>
          <button className="modal-close" onClick={onClose}>×</button>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="edit-company-form">
          <div className="form-group">
            <label htmlFor="name">ชื่อบริษัท *</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              disabled={loading}
              className="form-input"
              placeholder="ชื่อบริษัท จำกัด"
            />
          </div>

          <div className="form-group">
            <label htmlFor="industry">ประเภทธุรกิจ *</label>
            <select
              id="industry"
              name="industry"
              value={formData.industry}
              onChange={handleChange}
              required
              disabled={loading}
              className="form-select"
            >
              <option value="">เลือกประเภทธุรกิจ</option>
              {industries.map(industry => (
                <option key={industry} value={industry}>
                  {industry}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="description">รายละเอียดบริษัท</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              disabled={loading}
              className="form-textarea"
              rows="3"
              placeholder="รายละเอียดเกี่ยวกับบริษัท..."
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="location">ที่ตั้ง</label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="กรุงเทพมหานคร"
              />
            </div>

            <div className="form-group">
              <label htmlFor="website">เว็บไซต์</label>
              <input
                type="url"
                id="website"
                name="website"
                value={formData.website}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="https://example.com"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="contact_email">อีเมลติดต่อ</label>
              <input
                type="email"
                id="contact_email"
                name="contact_email"
                value={formData.contact_email}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="contact@example.com"
              />
            </div>

            <div className="form-group">
              <label htmlFor="contact_phone">เบอร์โทรติดต่อ</label>
              <input
                type="tel"
                id="contact_phone"
                name="contact_phone"
                value={formData.contact_phone}
                onChange={handleChange}
                disabled={loading}
                className="form-input"
                placeholder="02-xxx-xxxx"
              />
            </div>
          </div>

          <div className="form-group">
            <label className="checkbox-label">
              <input
                type="checkbox"
                name="is_active"
                checked={formData.is_active}
                onChange={handleChange}
                disabled={loading}
              />
              บริษัทใช้งานได้
            </label>
          </div>

          <div className="modal-actions">
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary"
              disabled={loading}
            >
              ยกเลิก
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'กำลังบันทึก...' : 'บันทึกการเปลี่ยนแปลง'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
