// frontend/src/components/Profile/ProfileInfo.jsx
import React, { useState } from 'react';

const ProfileInfo = ({ profileData, onUpdateProfile }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    full_name: profileData?.full_name || '',
    email: profileData?.email || '',
    phone: profileData?.phone || ''
  });
  const [loading, setLoading] = useState(false);
  const [imageFile, setImageFile] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);

  const handleEdit = () => {
    setIsEditing(true);
    setFormData({
      full_name: profileData?.full_name || '',
      email: profileData?.email || '',
      phone: profileData?.phone || ''
    });
  };

  const handleCancel = () => {
    setIsEditing(false);
    setImageFile(null);
    setPreviewImage(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // ตรวจสอบขนาดไฟล์ (5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert('ไฟล์รูปภาพต้องมีขนาดไม่เกิน 5MB');
        return;
      }

      // ตรวจสอบประเภทไฟล์
      const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png'];
      if (!allowedTypes.includes(file.type)) {
        alert('รองรับเฉพาะไฟล์ JPG, JPEG, PNG เท่านั้น');
        return;
      }

      setImageFile(file);
      
      // แสดงตัวอย่างรูป
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem('token');

      // อัปเดตข้อมูลโปรไฟล์
      const profileResponse = await fetch('http://localhost:8000/api/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (!profileResponse.ok) {
        const errorData = await profileResponse.json();
        throw new Error(errorData.detail || 'Failed to update profile');
      }

      // อัปโหลดรูปภาพ (ถ้ามี)
      if (imageFile) {
        const imageFormData = new FormData();
        imageFormData.append('file', imageFile);

        const imageResponse = await fetch('http://localhost:8000/api/profile/upload-image', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`
          },
          body: imageFormData
        });

        if (!imageResponse.ok) {
          console.error('Failed to upload image');
        }
      }

      alert('อัปเดตข้อมูลเรียบร้อยแล้ว');
      setIsEditing(false);
      setImageFile(null);
      setPreviewImage(null);
      onUpdateProfile(); // รีเฟรชข้อมูล

    } catch (error) {
      console.error('Error updating profile:', error);
      alert(`เกิดข้อผิดพลาด: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="profile-info-section">
      <div className="section-header">
        <h2>ข้อมูลส่วนตัว</h2>
        {!isEditing && (
          <button 
            className="btn-edit"
            onClick={handleEdit}
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
              <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"/>
              <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"/>
            </svg>
            แก้ไข
          </button>
        )}
      </div>

      {isEditing ? (
        <form onSubmit={handleSubmit} className="profile-form">
          {/* อัปโหลดรูปภาพ */}
          <div className="form-group image-upload">
            <label>รูปโปรไฟล์</label>
            <div className="image-upload-area">
              <div className="current-image">
                {previewImage ? (
                  <img src={previewImage} alt="Preview" />
                ) : profileData?.profile_image ? (
                  <img src={`http://localhost:8000${profileData.profile_image}`} alt="Current" />
                ) : (
                  <div className="no-image">ไม่มีรูปภาพ</div>
                )}
              </div>
              <div className="upload-controls">
                <input
                  type="file"
                  id="profile-image"
                  accept="image/jpeg,image/jpg,image/png"
                  onChange={handleImageChange}
                  className="file-input"
                />
                <label htmlFor="profile-image" className="btn-upload">
                  เลือกรูปภาพ
                </label>
                <small>รองรับ JPG, PNG ขนาดไม่เกิน 5MB</small>
              </div>
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="full_name">ชื่อ-นามสกุล *</label>
              <input
                type="text"
                id="full_name"
                name="full_name"
                value={formData.full_name}
                onChange={handleInputChange}
                required
                className="form-input"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">อีเมล *</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                className="form-input"
                disabled={loading}
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="phone">เบอร์โทรศัพท์</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                className="form-input"
                disabled={loading}
                placeholder="080-xxx-xxxx"
              />
            </div>

            <div className="form-group">
              <label htmlFor="created_at">วันที่สมัครสมาชิก</label>
              <input
                type="text"
                value={formatDate(profileData?.created_at)}
                readOnly
                className="form-input readonly"
              />
            </div>
          </div>

          <div className="form-actions">
            <button
              type="button"
              onClick={handleCancel}
              className="btn-cancel"
              disabled={loading}
            >
              ยกเลิก
            </button>
            <button
              type="submit"
              className="btn-save"
              disabled={loading}
            >
              {loading ? 'กำลังบันทึก...' : 'บันทึกการเปลี่ยนแปลง'}
            </button>
          </div>
        </form>
      ) : (
        <div className="profile-display">
          <div className="info-grid">
            <div className="info-card">
              <div className="info-header">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
                  <circle cx="12" cy="7" r="4"/>
                </svg>
                <span>ชื่อ-นามสกุล</span>
              </div>
              <p>{profileData?.full_name || 'admin_test'}</p>
            </div>

            <div className="info-card">
              <div className="info-header">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"/>
                  <polyline points="22,6 12,13 2,6"/>
                </svg>
                <span>อีเมล</span>
              </div>
              <p>{profileData?.email || 'admin@internscreen.com'}</p>
            </div>

            <div className="info-card">
              <div className="info-header">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M22 16.92v3a2 2 0 0 1-2.18 2 19.79 19.79 0 0 1-8.63-3.07 19.5 19.5 0 0 1-6-6 19.79 19.79 0 0 1-3.07-8.67A2 2 0 0 1 4.11 2h3a2 2 0 0 1 2 1.72 12.84 12.84 0 0 0 .7 2.81 2 2 0 0 1-.45 2.11L8.09 9.91a16 16 0 0 0 6 6l1.27-1.27a2 2 0 0 1 2.11-.45 12.84 12.84 0 0 0 2.81.7A2 2 0 0 1 22 16.92z"/>
                </svg>
                <span>เบอร์โทรศัพท์</span>
              </div>
              <p>{profileData?.phone || '098-999-9999'}</p>
            </div>

            <div className="info-card">
              <div className="info-header">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"/>
                  <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"/>
                </svg>
                <span>สถานะ</span>
              </div>
              <span className="status-badge active">ใช้งาน</span>
            </div>

            <div className="info-card">
              <div className="info-header">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <rect x="3" y="4" width="18" height="18" rx="2" ry="2"/>
                  <line x1="16" y1="2" x2="16" y2="6"/>
                  <line x1="8" y1="2" x2="8" y2="6"/>
                  <line x1="3" y1="10" x2="21" y2="10"/>
                </svg>
                <span>วันที่สมัคร</span>
              </div>
              <p>{formatDate(profileData?.created_at) || '5/6/2568'}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfileInfo;
