// frontend/src/components/Navbar/Navbar.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../../services/authService';
import '../../styles/navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  // ตรวจสอบสถานะ login เมื่อ component mount
  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    if (currentUser) {
      setUser(currentUser);
    }
  }, []);

  // ปิด dropdown เมื่อคลิกข้างนอก
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // ฟังก์ชัน logout
  const handleLogout = async () => {
    try {
      await authService.logout();
      setUser(null);
      setIsDropdownOpen(false);
      
      // แสดงข้อความ logout
      alert('ออกจากระบบเรียบร้อยแล้ว');
      
      // เปลี่ยนเส้นทางไปหน้าหลัก
      navigate('/', { replace: true });
    } catch (error) {
      console.error('Logout error:', error);
      alert('เกิดข้อผิดพลาดในการออกจากระบบ');
    }
  };

  // ฟังก์ชันไปหน้า profile (จะสร้างทีหลัง)
  const handleProfile = () => {
    setIsDropdownOpen(false);
    navigate('/profile');
  };

  // ฟังก์ชันสำหรับแสดงชื่อย่อ
  const getInitials = (fullName) => {
    if (!fullName) return 'U';
    
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return (names[0][0] + names[1][0]).toUpperCase();
    }
    return names[0][0].toUpperCase();
  };

  // ฟังก์ชันแสดงชื่อสั้น
  const getDisplayName = (fullName) => {
    if (!fullName) return 'User';
    
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return names[0]; // แสดงแค่ชื่อ
    }
    return fullName;
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <a href="/" className="navbar-logo">
          InternScreen
        </a>
        
        <div className="navbar-menu">
          <div className="navbar-links">
            <a href="/" className="navbar-link">หน้าหลัก</a>
            <a href="/companies" className="navbar-link">บริษัททั้งหมด</a>
          </div>
          
          <div className="navbar-auth">
            {user ? (
              // แสดง User Menu เมื่อ login แล้ว
              <div className="user-menu" ref={dropdownRef}>
                <button 
                  className="user-button"
                  onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                >
                  <div className="user-avatar">
                    {getInitials(user.full_name)}
                  </div>
                  <span className="user-name">
                    {getDisplayName(user.full_name)}
                  </span>
                  <svg 
                    className={`dropdown-arrow ${isDropdownOpen ? 'open' : ''}`}
                    width="16" 
                    height="16" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor"
                  >
                    <polyline points="6,9 12,15 18,9"></polyline>
                  </svg>
                </button>

                {/* Dropdown Menu */}
                {isDropdownOpen && (
                  <div className="dropdown-menu">
                    <div className="dropdown-header">
                      <div className="user-info">
                        <p className="user-full-name">{user.full_name}</p>
                        <p className="user-email">{user.email}</p>
                        <span className="user-role">{user.user_type}</span>
                      </div>
                    </div>
                    
                    <div className="dropdown-divider"></div>
                    
                    <div className="dropdown-items">
                      <button 
                        className="dropdown-item"
                        onClick={handleProfile}
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                          <circle cx="12" cy="7" r="4"></circle>
                        </svg>
                        แก้ไขโปรไฟล์
                      </button>
                      
                      <button 
                        className="dropdown-item logout"
                        onClick={handleLogout}
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
                          <polyline points="16,17 21,12 16,7"></polyline>
                          <line x1="21" y1="12" x2="9" y2="12"></line>
                        </svg>
                        ออกจากระบบ
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              // แสดง Login/Register เมื่อยังไม่ login
              <>
                <a href="/login" className="btn btn-secondary">Login</a>
                <a href="/register" className="btn btn-primary">Register</a>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
