// frontend/src/components/Admin/AdminDashboard.jsx - With Inline Modals
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import adminService from '../../services/adminService';
import CompanyManagement from './CompanyManagement';
import LoadingSpinner from '../LoadingSpinner/LoadingSpinner';

const AdminDashboard = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  
  const [activeTab, setActiveTab] = useState('overview');
  const [dashboardData, setDashboardData] = useState(null);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [usersLoading, setUsersLoading] = useState(false);
  const [error, setError] = useState('');
  
  // Pagination à¹à¸¥à¸° Filter states
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [userTypeFilter, setUserTypeFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  
  // Modal states
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [modalLoading, setModalLoading] = useState(false);

  // Real-time search
  useEffect(() => {
    if (activeTab === 'users') {
      const delayedSearch = setTimeout(() => {
        if (searchTerm || userTypeFilter || statusFilter) {
          setCurrentPage(1);
          loadUsers(1, searchTerm, userTypeFilter, statusFilter);
        }
      }, 500);

      return () => clearTimeout(delayedSearch);
    }
  }, [searchTerm, userTypeFilter, statusFilter, activeTab]);

  // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸š permission
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }

    if (!user || user.user_type !== 'Admin') {
      navigate('/dashboard');
      return;
    }

    loadDashboardData();
  }, [user, isAuthenticated, navigate]);

  // Auto-load users when switching to users tab
  useEffect(() => {
  if (activeTab === 'users') {
    const delayedSearch = setTimeout(() => {
      if (searchTerm || userTypeFilter || statusFilter) {
        setCurrentPage(1);
        loadUsers(1, searchTerm, userTypeFilter, statusFilter);
      }
    }, 500);

    return () => clearTimeout(delayedSearch);
  }
}, [searchTerm, userTypeFilter, statusFilter, activeTab]); // à¸¥à¸š users.length à¸­à¸­à¸

// à¹à¸¥à¸°
useEffect(() => {
  if (activeTab === 'users' && users.length === 0) {
    loadUsers(1, '', '', '');
  }
}, [activeTab]); // à¸¥à¸š users.length à¸­à¸­à¸

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const result = await adminService.getDashboardStats();
      if (result.success) {
        setDashboardData(result.data);
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Dashboard loading error:', error);
      setError('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥ Dashboard');
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async (page = 1, search = '', userType = '', status = '') => {
    try {
      setUsersLoading(true);
      setError('');
      
      const result = await adminService.getUsers({
        page,
        limit: 10,
        search: search.trim(),
        user_type: userType || null,
        is_active: status === 'active' ? true : status === 'inactive' ? false : null
      });
      
      if (result.success) {
        setUsers(result.data);
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Users loading error:', error);
      setError('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸£à¸²à¸¢à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰');
    } finally {
      setUsersLoading(false);
    }
  };

  // Handle Edit User
  const handleEditUser = async (userId) => {
    try {
      setModalLoading(true);
      const result = await adminService.getUserById(userId);
      if (result.success) {
        setSelectedUser(result.data);
        setShowEditModal(true);
      } else {
        alert('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰: ' + result.error);
      }
    } catch (error) {
      console.error('Get user error:', error);
      alert('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰');
    } finally {
      setModalLoading(false);
    }
  };

  // Handle Delete User
  const handleDeleteUser = async (userId, username) => {
    if (window.confirm(`à¸„à¸¸à¸“à¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰ "${username}" à¸«à¸£à¸·à¸­à¹„à¸¡à¹ˆ?`)) {
      try {
        const result = await adminService.deleteUser(userId);
        if (result.success) {
          alert('à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
          loadUsers(currentPage, searchTerm, userTypeFilter, statusFilter);
          loadDashboardData();
        } else {
          alert(result.error);
        }
      } catch (error) {
        console.error('Delete user error:', error);
        alert('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰');
      }
    }
  };

  // Format date
  const formatDate = (dateString) => {
    if (!dateString) return 'à¹„à¸¡à¹ˆà¹€à¸„à¸¢à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸š';
    return new Date(dateString).toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Handle Modal Success
  const handleModalSuccess = () => {
    loadUsers(currentPage, searchTerm, userTypeFilter, statusFilter);
    loadDashboardData();
    setShowEditModal(false);
    setShowCreateModal(false);
    setSelectedUser(null);
  };

  // Handle Modal Close
  const handleModalClose = () => {
    setShowEditModal(false);
    setShowCreateModal(false);
    setSelectedUser(null);
  };

  if (loading) {
    return <LoadingSpinner size="large" message="à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥ Dashboard..." />;
  }

  return (
    <div className="admin-dashboard">
      <div className="admin-container">
        {/* Header */}
        <div className="admin-header">
          <h1 className="admin-title">Admin Dashboard</h1>
          <p className="admin-subtitle">à¸£à¸°à¸šà¸šà¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹à¸¥à¸°à¸šà¸£à¸´à¸©à¸±à¸—</p>
        </div>

        {/* Tabs Navigation */}
        <div className="dashboard-tabs">
          <button 
            className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            ðŸ“Š à¸ à¸²à¸žà¸£à¸§à¸¡
          </button>
          <button 
            className={`tab-button ${activeTab === 'users' ? 'active' : ''}`}
            onClick={() => setActiveTab('users')}
          >
            ðŸ‘¥ à¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰
          </button>
          <button 
            className={`tab-button ${activeTab === 'companies' ? 'active' : ''}`}
            onClick={() => setActiveTab('companies')}
          >
            ðŸ¢ à¸ˆà¸±à¸”à¸à¸²à¸£à¸šà¸£à¸´à¸©à¸±à¸—
          </button>
        </div>

        {/* Error Display */}
        {error && (
          <div className="error-banner">
            <div className="error-content">
              <span className="error-icon">âš ï¸</span>
              <span className="error-text">{error}</span>
              <button 
                className="error-close"
                onClick={() => setError('')}
              >
                Ã—
              </button>
            </div>
          </div>
        )}

        {/* Tab Content */}
        <div className="tab-content">
          {/* Overview Tab */}
          {activeTab === 'overview' && dashboardData && (
            <div className="overview-tab">
              <div className="stats-grid">
                <div className="stat-card users">
                  <div className="stat-icon">ðŸ‘¥</div>
                  <div className="stat-info">
                    <h3>{dashboardData.total_users}</h3>
                    <p>à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”</p>
                    <span className="stat-detail">
                      {dashboardData.active_users} à¸„à¸™à¹ƒà¸Šà¹‰à¸‡à¸²à¸™à¹„à¸”à¹‰
                    </span>
                  </div>
                </div>

                <div className="stat-card companies">
                  <div className="stat-icon">ðŸ¢</div>
                  <div className="stat-info">
                    <h3>{dashboardData.total_companies}</h3>
                    <p>à¸šà¸£à¸´à¸©à¸±à¸—à¸—à¸µà¹ˆà¸¥à¸‡à¸—à¸°à¹€à¸šà¸µà¸¢à¸™</p>
                    <span className="stat-detail">
                      {dashboardData.active_companies} à¸šà¸£à¸´à¸©à¸±à¸—à¹ƒà¸Šà¹‰à¸‡à¸²à¸™à¹„à¸”à¹‰
                    </span>
                  </div>
                </div>

                <div className="stat-card hr">
                  <div className="stat-icon">ðŸ‘”</div>
                  <div className="stat-info">
                    <h3>{dashboardData.total_hr}</h3>
                    <p>HR à¹ƒà¸™à¸£à¸°à¸šà¸š</p>
                    <span className="stat-detail">
                      {dashboardData.hr_with_companies} à¸„à¸™à¸¡à¸µà¸šà¸£à¸´à¸©à¸±à¸—
                    </span>
                  </div>
                </div>

                <div className="stat-card students">
                  <div className="stat-icon">ðŸŽ“</div>
                  <div className="stat-info">
                    <h3>{dashboardData.total_students}</h3>
                    <p>à¸™à¸±à¸à¸¨à¸¶à¸à¸©à¸²</p>
                    <span className="stat-detail">
                      à¹€à¸‚à¹‰à¸²à¸£à¹ˆà¸§à¸¡à¸¥à¹ˆà¸²à¸ªà¸¸à¸” {formatDate(dashboardData.latest_user_created)}
                    </span>
                  </div>
                </div>
              </div>

              <div className="actions-section">
                <div className="section-card">
                  <h3>à¸à¸²à¸£à¸”à¸³à¹€à¸™à¸´à¸™à¸à¸²à¸£à¸”à¹ˆà¸§à¸™</h3>
                  <div className="actions-grid">
                    <button 
                      className="action-card"
                      onClick={() => setActiveTab('users')}
                    >
                      <div className="action-icon">ðŸ‘¥</div>
                      <div className="action-text">
                        <h4>à¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</h4>
                        <p>à¹€à¸žà¸´à¹ˆà¸¡ à¹à¸à¹‰à¹„à¸‚ à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</p>
                      </div>
                    </button>
                    
                    <button 
                      className="action-card"
                      onClick={() => setActiveTab('companies')}
                    >
                      <div className="action-icon">ðŸ¢</div>
                      <div className="action-text">
                        <h4>à¸ˆà¸±à¸”à¸à¸²à¸£à¸šà¸£à¸´à¸©à¸±à¸—</h4>
                        <p>à¹€à¸žà¸´à¹ˆà¸¡à¸šà¸£à¸´à¸©à¸±à¸—à¹à¸¥à¸°à¸à¸³à¸«à¸™à¸” HR</p>
                      </div>
                    </button>
                    
                    <button 
                      className="action-card"
                      onClick={() => setShowCreateModal(true)}
                    >
                      <div className="action-icon">âž•</div>
                      <div className="action-text">
                        <h4>à¹€à¸žà¸´à¹ˆà¸¡à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹ƒà¸«à¸¡à¹ˆ</h4>
                        <p>à¸ªà¸£à¹‰à¸²à¸‡à¸šà¸±à¸à¸Šà¸µà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹ƒà¸«à¸¡à¹ˆ</p>
                      </div>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Users Management Tab */}
          {activeTab === 'users' && (
            <div className="users-tab">
              <div className="section-header">
                <h2>ðŸ‘¥ à¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</h2>
                <button 
                  className="btn btn-primary"
                  onClick={() => setShowCreateModal(true)}
                  disabled={modalLoading}
                >
                  + à¹€à¸žà¸´à¹ˆà¸¡à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹ƒà¸«à¸¡à¹ˆ
                </button>
              </div>

              {/* Filters */}
              <div className="filters">
                <div className="filter-group">
                  <input
                    type="text"
                    placeholder="ðŸ” à¸„à¹‰à¸™à¸«à¸²à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
