                  >
                    <option value="">à¸›à¸£à¸°à¹€à¸ à¸—à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”</option>
                    <option value="Student">à¸™à¸±à¸à¸¨à¸¶à¸à¸©à¸²</option>
                    <option value="HR">HR</option>
                    <option value="Admin">à¸œà¸¹à¹‰à¸”à¸¹à¹à¸¥à¸£à¸°à¸šà¸š</option>
                  </select>
                </div>

                <div className="filter-group">
                  <select
                    value={statusFilter}
                    onChange={(e) => setStatusFilter(e.target.value)}
                    className="filter-select"
                  >
                    <option value="">à¸ªà¸–à¸²à¸™à¸°à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”</option>
                    <option value="active">à¹ƒà¸Šà¹‰à¸‡à¸²à¸™à¹„à¸”à¹‰</option>
                    <option value="inactive">à¹„à¸¡à¹ˆà¹ƒà¸Šà¹‰à¸‡à¸²à¸™</option>
                  </select>
                </div>

                <button
                  onClick={() => {
                    setSearchTerm('');
                    setUserTypeFilter('');
                    setStatusFilter('');
                    setCurrentPage(1);
                    loadUsers(1, '', '', '');
                  }}
                  className="btn btn-secondary"
                >
                  à¸¥à¹‰à¸²à¸‡à¸•à¸±à¸§à¸à¸£à¸­à¸‡
                </button>
              </div>

              {/* Users Table */}
              <div className="table-container">
                {usersLoading ? (
                  <LoadingSpinner message="à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”à¸£à¸²à¸¢à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰..." />
                ) : (
                  <table className="users-table">
                    <thead>
                      <tr>
                        <th>à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</th>
                        <th>à¸­à¸µà¹€à¸¡à¸¥</th>
                        <th>à¸›à¸£à¸°à¹€à¸ à¸—</th>
                        <th>à¸ªà¸–à¸²à¸™à¸°</th>
                        <th>à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸šà¸¥à¹ˆà¸²à¸ªà¸¸à¸”</th>
                        <th>à¸à¸²à¸£à¸”à¸³à¹€à¸™à¸´à¸™à¸à¸²à¸£</th>
                      </tr>
                    </thead>
                    <tbody>
                      {users.length > 0 ? users.map(user => (
                        <tr key={user.id}>
                          <td>
                            <div className="user-info">
                              <div className="user-avatar">
                                {user.full_name ? user.full_name.charAt(0).toUpperCase() : user.username.charAt(0).toUpperCase()}
                              </div>
                              <div className="user-details">
                                <div className="user-name">{user.full_name || user.username}</div>
                                <div className="user-username">@{user.username}</div>
                              </div>
                            </div>
                          </td>
