// frontend/src/components/Profile/ChangePassword.jsx
import React, { useState } from 'react';

const ChangePassword = () => {
  const [formData, setFormData] = useState({
    current_password: '',
    new_password: '',
    confirm_password: ''
  });
  const [loading, setLoading] = useState(false);
  const [showPasswords, setShowPasswords] = useState({
    current: false,
    new: false,
    confirm: false
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const togglePasswordVisibility = (field) => {
    setShowPasswords(prev => ({
      ...prev,
      [field]: !prev[field]
    }));
  };

  const validatePassword = () => {
    if (formData.new_password.length < 6) {
      alert('รหัสผ่านใหม่ต้องมีความยาวอย่างน้อย 6 ตัวอักษร');
      return false;
    }

    if (formData.new_password !== formData.confirm_password) {
      alert('รหัสผ่านใหม่และยืนยันรหัสผ่านไม่ตรงกัน');
      return false;
    }

    if (formData.current_password === formData.new_password) {
      alert('รหัสผ่านใหม่ต้องไม่เหมือนกับรหัสผ่านเดิม');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validatePassword()) {
      return;
    }

    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8000/api/profile/change-password', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          current_password: formData.current_password,
          new_password: formData.new_password
        })
      });

      if (response.ok) {
        alert('เปลี่ยนรหัสผ่านเรียบร้อยแล้ว');
        setFormData({
          current_password: '',
          new_password: '',
          confirm_password: ''
        });
      } else {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to change password');
      }

    } catch (error) {
      console.error('Error changing password:', error);
      if (error.message.includes('Current password is incorrect')) {
        alert('รหัสผ่านปัจจุบันไม่ถูกต้อง');
      } else {
        alert(`เกิดข้อผิดพลาด: ${error.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const getPasswordStrength = (password) => {
    if (!password) return { strength: 0, text: '', color: '#ccc' };
    
    let strength = 0;
    let text = '';
    let color = '';

    // ตรวจสอบความยาว
    if (password.length >= 6) strength += 1;
    if (password.length >= 8) strength += 1;

    // ตรวจสอบตัวอักษรพิมพ์เล็กและใหญ่
    if (/[a-z]/.test(password) && /[A-Z]/.test(password)) strength += 1;

    // ตรวจสอบตัวเลข
    if (/\d/.test(password)) strength += 1;

    // ตรวจสอบสัญลักษณ์พิเศษ
    if (/[!@#$%^&*(),.?":{}|<>]/.test(password)) strength += 1;

    switch (strength) {
      case 0:
      case 1:
        text = 'อ่อน';
        color = '#f44336';
        break;
      case 2:
        text = 'ปานกลาง';
        color = '#ff9800';
        break;
      case 3:
        text = 'ดี';
        color = '#2196f3';
        break;
      case 4:
      case 5:
        text = 'แข็งแรง';
        color = '#4caf50';
        break;
      default:
        text = '';
        color = '#ccc';
    }

    return { strength, text, color };
  };

  const passwordStrength = getPasswordStrength(formData.new_password);

  return (
    <div className="change-password-section">
      <div className="section-header">
        <h2>เปลี่ยนรหัสผ่าน</h2>
      </div>

      <form onSubmit={handleSubmit} className="password-form">
        <div className="form-group">
          <label htmlFor="current_password">รหัสผ่านปัจจุบัน *</label>
          <div className="password-input-wrapper">
            <input
              type={showPasswords.current ? "text" : "password"}
              id="current_password"
              name="current_password"
              value={formData.current_password}
              onChange={handleInputChange}
              required
              className="form-input"
              disabled={loading}
              placeholder="กรอกรหัสผ่านปัจจุบัน"
            />
            <button
              type="button"
              className="password-toggle"
              onClick={() => togglePasswordVisibility('current')}
            >
              {showPasswords.current ? (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94M9.9 4.24A9.12 9.12 0 0 1 12 4c7 0 11 8 11 8a18.5 18.5 0 0 1-2.16 3.19m-6.72-1.07a3 3 0 1 1-4.24-4.24"/>
                  <line x1="1" y1="1" x2="23" y2="23"/>
                </svg>
              ) : (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"/>
                  <circle cx="12" cy="12" r="3"/>
                </svg>
              )}
            </button>
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="new_password">รหัสผ่านใหม่ *</label>
          <div className="password-input-wrapper">
            <input
              type={showPasswords.new ? "text" : "password"}
              id="new_password"
              name="new_password"
              value={formData.new_password}
              onChange={handleInputChange}
              required
              className="form-input"
              disabled={loading}
              placeholder="กรอกรหัสผ่านใหม่"
            />
            <button
              type="button"
              className="password-toggle"
              onClick={() => togglePasswordVisibility('new')}
            >
              {showPasswords.new ? (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94M9.9 4.24A9.12 9.12 0 0 1 12 4c7 0 11 8 11 8a18.5 18.5 0 0 1-2.16 3.19m-6.72-1.07a3 3 0 1 1-4.24-4.24"/>
                  <line x1="1" y1="1" x2="23" y2="23"/>
                </svg>
              ) : (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"/>
                  <circle cx="12" cy="12" r="3"/>
                </svg>
              )}
            </button>
          </div>
          
          {/* Password Strength Indicator */}
          {formData.new_password && (
            <div className="password-strength">
              <div className="strength-bar">
                <div 
                  className="strength-fill" 
                  style={{ 
                    width: `${(passwordStrength.strength / 5) * 100}%`,
                    backgroundColor: passwordStrength.color
                  }}
                ></div>
              </div>
              <span 
                className="strength-text" 
                style={{ color: passwordStrength.color }}
              >
                ความแข็งแรง: {passwordStrength.text}
              </span>
            </div>
          )}
          
          <small className="password-hint">รหัสผ่านต้องมีความยาวอย่างน้อย 6 ตัวอักษร</small>
        </div>

        <div className="form-group">
          <label htmlFor="confirm_password">ยืนยันรหัสผ่านใหม่ *</label>
          <div className="password-input-wrapper">
            <input
              type={showPasswords.confirm ? "text" : "password"}
              id="confirm_password"
              name="confirm_password"
              value={formData.confirm_password}
              onChange={handleInputChange}
              required
              className="form-input"
              disabled={loading}
              placeholder="ยืนยันรหัสผ่านใหม่"
            />
            <button
              type="button"
              className="password-toggle"
              onClick={() => togglePasswordVisibility('confirm')}
            >
              {showPasswords.confirm ? (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94M9.9 4.24A9.12 9.12 0 0 1 12 4c7 0 11 8 11 8a18.5 18.5 0 0 1-2.16 3.19m-6.72-1.07a3 3 0 1 1-4.24-4.24"/>
                  <line x1="1" y1="1" x2="23" y2="23"/>
                </svg>
              ) : (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"/>
                  <circle cx="12" cy="12" r="3"/>
                </svg>
              )}
            </button>
          </div>
          
          {/* Password Match Indicator */}
          {formData.confirm_password && (
            <div className="password-match">
              {formData.new_password === formData.confirm_password ? (
                <span className="match-success">
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <polyline points="20,6 9,17 4,12"/>
                  </svg>
                  รหัสผ่านตรงกัน
                </span>
              ) : (
                <span className="match-error">
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <line x1="18" y1="6" x2="6" y2="18"/>
                    <line x1="6" y1="6" x2="18" y2="18"/>
                  </svg>
                  รหัสผ่านไม่ตรงกัน
                </span>
              )}
            </div>
          )}
        </div>

        <div className="form-actions">
          <button
            type="submit"
            className="btn-change-password"
            disabled={loading || formData.new_password !== formData.confirm_password}
          >
            {loading ? (
              <>
                <svg className="spinner-small" width="16" height="16" viewBox="0 0 24 24">
                  <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="32" strokeDashoffset="32">
                    <animate attributeName="stroke-dashoffset" dur="1s" values="32;0" repeatCount="indefinite"/>
                    <animate attributeName="stroke-dasharray" dur="1s" values="32;8" repeatCount="indefinite"/>
                  </circle>
                </svg>
                กำลังเปลี่ยนรหัสผ่าน...
              </>
            ) : (
              'บันทึกการเปลี่ยนแปลง'
            )}
          </button>
        </div>
      </form>

      <div className="password-tips">
        <h3>⚠️ ข้อแนะนำในการตั้งรหัสผ่าน</h3>
        <ul>
          <li>ใช้รหัสผ่านที่มีความยาวอย่างน้อย 8 ตัวอักษร</li>
          <li>ผสมผสานตัวอักษรพิมพ์เล็ก พิมพ์ใหญ่ ตัวเลข และสัญลักษณ์</li>
          <li>หลีกเลี่ยงการใช้ข้อมูลส่วนตัว เช่น ชื่อ วันเกิด เบอร์โทร</li>
          <li>ไม่ควรใช้รหัสผ่านเดียวกันในหลายบัญชี</li>
          <li>เปลี่ยนรหัสผ่านเป็นประจำอย่างน้อย 3 เดือนครั้ง</li>
        </ul>
      </div>

      <div className="security-info">
        <h3>🔒 ข้อมูลเพิ่มเติมเกี่ยวกับความปลอดภัย</h3>
        <div className="security-grid">
          <div className="security-item">
            <div className="security-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
                <circle cx="12" cy="16" r="1"/>
                <path d="M7 11V7a5 5 0 0 1 10 0v4"/>
              </svg>
            </div>
            <div className="security-content">
              <h4>การเข้ารหัส</h4>
              <p>รหัสผ่านของคุณถูกเข้ารหัสอย่างปลอดภัยด้วยเทคโนโลยีสมัยใหม่</p>
            </div>
          </div>

          <div className="security-item">
            <div className="security-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path d="M9 12l2 2 4-4"/>
                <path d="M21 12c.552 0 1-.448 1-1V8c0-.552-.448-1-1-1h-1V6a3 3 0 0 0-3-3H7a3 3 0 0 0-3 3v1H3c-.552 0-1 .448-1 1v3c0 .552.448 1 1 1h1v1a3 3 0 0 0 3 3h10a3 3 0 0 0 3-3v-1h1z"/>
              </svg>
            </div>
            <div className="security-content">
              <h4>การยืนยันตัวตน</h4>
              <p>ระบบจะตรวจสอบรหัสผ่านเดิมก่อนอนุญาตให้เปลี่ยนแปลง</p>
            </div>
          </div>

          <div className="security-item">
            <div className="security-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path d="M18 8A6 6 0 0 0 6 8c0 7-3 9-3 9h18s-3-2-3-9"/>
                <path d="M13.73 21a2 2 0 0 1-3.46 0"/>
              </svg>
            </div>
            <div className="security-content">
              <h4>การแจ้งเตือน</h4>
              <p>คุณจะได้รับการแจ้งเตือนเมื่อมีการเปลี่ยนแปลงรหัสผ่าน</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChangePassword;
