// frontend/src/components/Admin/AdminDashboard.jsx - Fixed Version
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import adminService from '../../services/adminService';
import CompanyManagement from './CompanyManagement';
import LoadingSpinner from '../LoadingSpinner/LoadingSpinner';

// Import Modal Components
import { CreateUserModal, EditUserModal } from './UserModals';

const AdminDashboard = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  
  const [activeTab, setActiveTab] = useState('overview');
  const [dashboardData, setDashboardData] = useState(null);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [usersLoading, setUsersLoading] = useState(false);
  const [error, setError] = useState('');
  
  // Pagination à¹à¸¥à¸° Filter states
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [userTypeFilter, setUserTypeFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  
  // Modal states - à¹à¸à¹‰à¹„à¸‚à¸à¸²à¸£à¸ˆà¸±à¸”à¸à¸²à¸£ state
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [modalLoading, setModalLoading] = useState(false);

  // Real-time search
  useEffect(() => {
    if (activeTab === 'users') {
      const delayedSearch = setTimeout(() => {
        if (users.length > 0 || searchTerm || userTypeFilter || statusFilter) {
          setCurrentPage(1);
          loadUsers(1, searchTerm, userTypeFilter, statusFilter);
        }
      }, 500);

      return () => clearTimeout(delayedSearch);
    }
  }, [searchTerm, userTypeFilter, statusFilter, activeTab]);

  // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸š permission
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }

    if (!user || user.user_type !== 'Admin') {
      navigate('/dashboard');
      return;
    }

    loadDashboardData();
  }, [user, isAuthenticated, navigate]);

  // Auto-load users when switching to users tab
  useEffect(() => {
    if (activeTab === 'users' && users.length === 0) {
      loadUsers(1, '', '', '');
    }
  }, [activeTab]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const result = await adminService.getDashboardStats();
      if (result.success) {
        setDashboardData(result.data);
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Dashboard loading error:', error);
      setError('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥ Dashboard');
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async (page = 1, search = '', userType = '', status = '') => {
    try {
      setUsersLoading(true);
      setError('');
      
      const result = await adminService.getUsers({
        page,
        limit: 10,
        search: search.trim(),
        user_type: userType || null,
        is_active: status === 'active' ? true : status === 'inactive' ? false : null
      });
      
      if (result.success) {
        setUsers(result.data);
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Users loading error:', error);
      setError('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸£à¸²à¸¢à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰');
    } finally {
      setUsersLoading(false);
    }
  };

  // Handle Edit User - à¹à¸à¹‰à¹„à¸‚à¹ƒà¸«à¹‰à¹à¸ªà¸”à¸‡ Modal
  const handleEditUser = async (userId) => {
    try {
      setModalLoading(true);
      const result = await adminService.getUserById(userId);
      if (result.success) {
        setSelectedUser(result.data);
