// frontend/src/components/Admin/AdminDashboard.jsx - Fixed Version
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import adminService from '../../services/adminService';
import companyService from '../../services/companyService';
import CompanyManagement from './CompanyManagement';
import LoadingSpinner from '../LoadingSpinner/LoadingSpinner';

const AdminDashboard = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  
  const [activeTab, setActiveTab] = useState('overview');
  const [dashboardData, setDashboardData] = useState(null);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [usersLoading, setUsersLoading] = useState(false);
  const [error, setError] = useState('');
  
  // Pagination à¹à¸¥à¸° Filter states
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [userTypeFilter, setUserTypeFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  
  // Modal states
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [modalLoading, setModalLoading] = useState(false);

  // Real-time search
  useEffect(() => {
    if (activeTab === 'users') {
      const delayedSearch = setTimeout(() => {
        if (users.length > 0 || searchTerm || userTypeFilter || statusFilter) {
          setCurrentPage(1);
          loadUsers(1, searchTerm, userTypeFilter, statusFilter);
        }
      }, 500);
      return () => clearTimeout(delayedSearch);
    }
  }, [searchTerm, userTypeFilter, statusFilter, activeTab]);

  // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸š permission
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }

    if (!user || (user.user_type !== 'Admin' && !user.roles?.includes('Admin'))) {
      alert('à¸„à¸¸à¸“à¹„à¸¡à¹ˆà¸¡à¸µà¸ªà¸´à¸—à¸˜à¸´à¹Œà¹€à¸‚à¹‰à¸²à¸–à¸¶à¸‡à¸«à¸™à¹‰à¸²à¸™à¸µà¹‰');
      navigate('/');
      return;
    }

    loadDashboardData();
    if (activeTab === 'users') {
      loadUsers();
    }
  }, [isAuthenticated, user, navigate, activeTab]);

  // â­ à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥ Dashboard - à¹€à¸žà¸´à¹ˆà¸¡ console.log
  const loadDashboardData = async () => {
    try {
      console.log('ðŸ”„ Loading dashboard data...');
      setLoading(true);
      const result = await adminService.getDashboardStats();
      
      if (result.success) {
        setDashboardData(result.data);
        console.log('âœ… Dashboard data loaded:', result.data);
      } else {
        console.error('âŒ Failed to load dashboard:', result.error);
        setError(result.error);
      }
    } catch (error) {
      console.error('ðŸ’¥ Dashboard error:', error);
      setError('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥');
    } finally {
      setLoading(false);
    }
  };

  // â­ à¹‚à¸«à¸¥à¸”à¸£à¸²à¸¢à¸à¸²à¸£ Users - à¹€à¸žà¸´à¹ˆà¸¡ console.log
  const loadUsers = async (page = 1, search = '', userType = '', status = '') => {
    try {
      console.log('ðŸ”„ Loading users...', { page, search, userType, status });
      setUsersLoading(true);
      
      const result = await adminService.getUsers({
        page,
        limit: 10,
        search,
        user_type: userType,
        is_active: status === '' ? null : status === 'active'
      });
      
      console.log('ðŸ“Š Users API result:', result);
      
      if (result.success) {
        setUsers(result.data);
        console.log('âœ… Users state updated, count:', result.data.length);
      } else {
        console.error('âŒ Failed to load users:', result.error);
        setError(result.error);
      }
    } catch (error) {
      console.error('ðŸ’¥ Load users error:', error);
      setError('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸£à¸²à¸¢à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰');
    } finally {
      setUsersLoading(false);
    }
  };

  // Handle Edit User
  const handleEditUser = async (userId) => {
    try {
      setModalLoading(true);
      const result = await adminService.getUserById(userId);
      if (result.success) {
        setSelectedUser(result.data);
        setShowEditModal(true);
      } else {
        alert('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰: ' + result.error);
      }
    } catch (error) {
      alert('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰');
    } finally {
      setModalLoading(false);
    }
  };

  // Handle Delete User
  const handleDeleteUser = async (userId, username) => {
    if (window.confirm(`à¸„à¸¸à¸“à¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰ "${username}" à¸«à¸£à¸·à¸­à¹„à¸¡à¹ˆ?`)) {
      try {
        const result = await adminService.deleteUser(userId);
        if (result.success) {
          alert('à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
          loadUsers(currentPage, searchTerm, userTypeFilter, statusFilter);
          loadDashboardData();
        } else {
          alert(result.error);
        }
      } catch (error) {
        alert('à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰');
      }
    }
  };

  // Format date
  const formatDate = (dateString) => {
    if (!dateString) return 'à¹„à¸¡à¹ˆà¹€à¸„à¸¢à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸š';
    return new Date(dateString).toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return <LoadingSpinner size="large" message="à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥ Dashboard..." />;
  }

  return (
    <div className="admin-dashboard">
      <div className="admin-container">
        {/* Header */}
        <div className="admin-header">
          <h1 className="admin-title">Admin Dashboard</h1>
          <p className="admin-subtitle">à¸£à¸°à¸šà¸šà¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹à¸¥à¸°à¸šà¸£à¸´à¸©à¸±à¸—</p>
        </div>

        {/* Tabs Navigation */}
        <div className="dashboard-tabs">
          <button 
            className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            ðŸ“Š à¸ à¸²à¸žà¸£à¸§à¸¡
          </button>
          <button 
            className={`tab-button ${activeTab === 'users' ? 'active' : ''}`}
            onClick={() => setActiveTab('users')}
          >
            ðŸ‘¥ à¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰
          </button>
          <button 
            className={`tab-button ${activeTab === 'companies' ? 'active' : ''}`}
            onClick={() => setActiveTab('companies')}
          >
            ðŸ¢ à¸ˆà¸±à¸”à¸à¸²à¸£à¸šà¸£à¸´à¸©à¸±à¸—
          </button>
        </div>

        {/* Tab Content */}
        <div className="dashboard-content">
          {/* Overview Tab */}
          {activeTab === 'overview' && (
            <div className="overview-tab">
              <div className="overview-cards">
                <div className="overview-card">
                  <div className="card-icon">ðŸ‘¥</div>
                  <div className="card-content">
                    <div className="card-number">{dashboardData?.total_users || 0}</div>
                    <div className="card-label">à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”</div>
                  </div>
                </div>
                
                <div className="overview-card">
                  <div className="card-icon">ðŸ¢</div>
                  <div className="card-content">
                    <div className="card-number">{dashboardData?.total_companies || 0}</div>
                    <div className="card-label">à¸šà¸£à¸´à¸©à¸±à¸—à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”</div>
                  </div>
                </div>
                
                <div className="overview-card">
                  <div className="card-icon">ðŸ’¼</div>
                  <div className="card-content">
                    <div className="card-number">{dashboardData?.total_hr_users || 0}</div>
                    <div className="card-label">HR à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</div>
                  </div>
                </div>
                
                <div className="overview-card">
                  <div className="card-icon">ðŸŽ“</div>
                  <div className="card-content">
                    <div className="card-number">{dashboardData?.total_students || 0}</div>
                    <div className="card-label">à¸™à¸±à¸à¸¨à¸¶à¸à¸©à¸²</div>
                  </div>
                </div>
              </div>

              <div className="quick-actions">
                <h3>à¸à¸²à¸£à¸”à¸³à¹€à¸™à¸´à¸™à¸à¸²à¸£à¸”à¹ˆà¸§à¸™</h3>
                <div className="actions-grid">
                  <button 
                    className="action-card"
                    onClick={() => setActiveTab('users')}
                  >
                    <div className="action-icon">ðŸ‘¥</div>
                    <div className="action-text">
                      <h4>à¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</h4>
                      <p>à¹€à¸žà¸´à¹ˆà¸¡ à¹à¸à¹‰à¹„à¸‚ à¸¥à¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</p>
                    </div>
                  </button>
                  
                  <button 
                    className="action-card"
                    onClick={() => setActiveTab('companies')}
                  >
                    <div className="action-icon">ðŸ¢</div>
                    <div className="action-text">
                      <h4>à¸ˆà¸±à¸”à¸à¸²à¸£à¸šà¸£à¸´à¸©à¸±à¸—</h4>
                      <p>à¹€à¸žà¸´à¹ˆà¸¡à¸šà¸£à¸´à¸©à¸±à¸—à¹à¸¥à¸°à¸à¸³à¸«à¸™à¸” HR</p>
                    </div>
                  </button>
                  
                  <button 
                    className="action-card"
                    onClick={() => setShowCreateModal(true)}
                  >
                    <div className="action-icon">âž•</div>
                    <div className="action-text">
                      <h4>à¹€à¸žà¸´à¹ˆà¸¡à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹ƒà¸«à¸¡à¹ˆ</h4>
                      <p>à¸ªà¸£à¹‰à¸²à¸‡à¸šà¸±à¸à¸Šà¸µà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹ƒà¸«à¸¡à¹ˆ</p>
                    </div>
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Users Management Tab */}
          {activeTab === 'users' && (
            <div className="users-tab">
              <div className="section-header">
                <h2>ðŸ‘¥ à¸ˆà¸±à¸”à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</h2>
                <button 
                  className="btn btn-primary"
                  onClick={() => setShowCreateModal(true)}
                >
                  + à¹€à¸žà¸´à¹ˆà¸¡à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¹ƒà¸«à¸¡à¹ˆ
                </button>
              </div>

              {/* Filters */}
              <div className="filters">
                <div className="filter-group">
                  <input
                    type="text"
                    placeholder="ðŸ” à¸„à¹‰à¸™à¸«à¸²à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                  />
                </div>
                
                <div className="filter-group">
                  <select
                    value={userTypeFilter}
                    onChange={(e) => setUserTypeFilter(e.target.value)}
                    className="filter-select"
                  >
                    <option value="">à¸—à¸¸à¸ Role</option>
                    <option value="Admin">Admin</option>
                    <option value="HR">HR</option>
                    <option value="Student">Student</option>
                  </select>
                </div>
                
                <div className="filter-group">
                  <select
                    value={statusFilter}
                    onChange={(e) => setStatusFilter(e.target.value)}
                    className="filter-select"
                  >
                    <option value="">à¸—à¸¸à¸à¸ªà¸–à¸²à¸™à¸°</option>
                    <option value="active">à¹€à¸›à¸´à¸”à¹ƒà¸Šà¹‰à¸‡à¸²à¸™</option>
                    <option value="inactive">à¸›à¸´à¸”à¹ƒà¸Šà¹‰à¸‡à¸²à¸™</option>
                  </select>
                </div>
              </div>

              {/* Users Table */}
              <div className="table-container">
                {usersLoading ? (
                  <LoadingSpinner message="à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”à¸£à¸²à¸¢à¸à¸²à¸£à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰..." />
                ) : (
                  <table className="users-table">
                    <thead>
                      <tr>
                        <th>à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰</th>
                        <th>à¸­à¸µà¹€à¸¡à¸¥</th>
                        <th>Role</th>
                        <th>à¸ªà¸–à¸²à¸™à¸°</th>
                        <th>à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸šà¸¥à¹ˆà¸²à¸ªà¸¸à¸”</th>
                        <th>à¸à¸²à¸£à¸ˆà¸±à¸”à¸à¸²à¸£</th>
                      </tr>
                    </thead>
                    <tbody>
                      {users.length > 0 ? (
                        users.map((user) => (
                          <tr key={user.id}>
                            <td>
                              <div className="user-info">
                                <div className="user-avatar-small">
                                  {user.full_name.charAt(0).toUpperCase()}
                                </div>
                                <div>
                                  <div className="user-name">{user.full_name}</div>
                                  <div className="username">@{user.username}</div>
                                </div>
                              </div>
                            </td>
                            <td>{user.email}</td>
                            <td>
                              <span className={`role-badge ${user.user_type.toLowerCase()}`}>
                                {user.user_type}
                              </span>
                            </td>
                            <td>
                              <span className={`status-badge ${user.is_active ? 'active' : 'inactive'}`}>
                                {user.is_active ? 'à¹€à¸›à¸´à¸”à¹ƒà¸Šà¹‰à¸‡à¸²à¸™' : 'à¸›à¸´à¸”à¹ƒà¸Šà¹‰à¸‡à¸²à¸™'}
                              </span>
                            </td>
                            <td>{formatDate(user.last_login)}</td>
                            <td>
                              <div className="action-buttons">
                                <button
                                  className="btn btn-sm btn-primary"
                                  onClick={() => handleEditUser(user.id)}
                                  disabled={modalLoading}
                                >
                                  âœï¸ à¹à¸à¹‰à¹„à¸‚
                                </button>
                                <button
                                  className="btn btn-sm btn-danger"
                                  onClick={() => handleDeleteUser(user.id, user.username)}
                                >
                                  ðŸ—‘ï¸ à¸¥à¸š
                                </button>
                              </div>
                            </td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="6" className="no-data">
                            {searchTerm || userTypeFilter || statusFilter ? 
                              'à¹„à¸¡à¹ˆà¸žà¸šà¸œà¸¹à¹‰à¹ƒà¸Šà¹‰à¸—à¸µà¹ˆà¸•à¸£à¸‡à¸à¸±à¸šà¹€à¸‡à¸·à¹ˆà¸­à¸™à¹„à¸‚à¸à¸²à¸£à¸„à¹‰à¸™à¸«à¸²' 
                              : 'à¹„à¸¡à¹ˆà¸¡à¸µà¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰'}
                          </td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                )}
              </div>

              {/* Pagination */}
              {users.length > 0 && (
                <div className="pagination">
                  <button 
                    className="btn btn-secondary"
                    disabled={currentPage === 1}
                    onClick={() => {
                      const newPage = currentPage - 1;
                      setCurrentPage(newPage);
                      loadUsers(newPage, searchTerm, userTypeFilter, statusFilter);
                    }}
                  >
                    â† à¸à¹ˆà¸­à¸™à¸«à¸™à¹‰à¸²
                  </button>
                  
                  <span className="page-info">à¸«à¸™à¹‰à¸² {currentPage}</span>
                  
                  <button 
                    className="btn btn-secondary"
                    disabled={users.length < 10}
                    onClick={() => {
                      const newPage = currentPage + 1;
                      setCurrentPage(newPage);
                      loadUsers(newPage, searchTerm, userTypeFilter, statusFilter);
                    }}
                  >
                    à¸–à¸±à¸”à¹„à¸› â†’
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Companies Management Tab */}
          {activeTab === 'companies' && (
            <div className="companies-tab">
              <CompanyManagement />
            </div>
          )}
        </div>
      </div>

      {/* â­ Edit User Modal - à¹à¸à¹‰à¹„à¸‚ onSuccess */}
      {showEditModal && selectedUser && (
        <EditUserModal
          user={selectedUser}
          onClose={() => {
            setShowEditModal(false);
            setSelectedUser(null);
          }}
          onSuccess={async () => {
            console.log('ðŸ”„ User edit successful, refreshing data...');
            
            try {
              // â­ à¸šà¸±à¸‡à¸„à¸±à¸š refresh à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”
              await Promise.all([
                loadUsers(currentPage, searchTerm, userTypeFilter, statusFilter),
                loadDashboardData()
              ]);
              
              console.log('âœ… Data refresh completed');
              
              // à¸›à¸´à¸” modal
              setShowEditModal(false);
              setSelectedUser(null);
              
            } catch (error) {
              console.error('âŒ Failed to refresh data:', error);
              
              // â­ à¸«à¸²à¸à¸¢à¸±à¸‡à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ à¹ƒà¸«à¹‰ reload à¸«à¸™à¹‰à¸²à¹€à¸§à¹‡à¸š
              console.log('ðŸ”„ Force reloading page...');
              window.location.reload();
            }
          }}
        />
      )}

      {/* Create User Modal */}
      {showCreateModal && (
        <CreateUserModal
          onClose={() => setShowCreateModal(false)}
          onSuccess={async () => {
            console.log('ðŸ”„ User create successful, refreshing data...');
            
            try {
              await Promise.all([
                loadUsers(currentPage, searchTerm, userTypeFilter, statusFilter),
                loadDashboardData()
              ]);
              
              setShowCreateModal(false);
              
            } catch (error) {
              console.error('âŒ Failed to refresh data after create:', error);
              window.location.reload();
            }
          }}
        />
      )}
    </div>
  );
};

// â­ Edit User Modal Component - à¹€à¸žà¸´à¹ˆà¸¡ Debug Logs
const EditUserModal = ({ user, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    username: user.username || '',
    full_name: user.full_name || '',
    email: user.email || '',
    phone: user.phone || '',
    user_type: user.user_type || 'Student',
    is_active: user.is_active !== undefined ? user.is_active : true,
    new_password: '',
    confirm_password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPasswordFields, setShowPasswordFields] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      console.log('ðŸ”§ Starting user update...', { userId: user.id, formData });

      if (formData.username.trim().length < 3) {
