// frontend/src/components/Navbar/Navbar.jsx - Final Version with ProfileContext
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useProfile } from '../../contexts/ProfileContext';
import '../../styles/navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const { user, logout: authLogout, isAuthenticated } = useAuth();
  const { 
    profileImageUrl, 
    initials, 
    displayName, 
    fullName, 
    email,
    loading: profileLoading 
  } = useProfile();
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  // ปิด dropdown เมื่อคลิกข้างนอก
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // ฟังก์ชัน logout
  const handleLogout = async () => {
    try {
      const result = await authLogout();
      setIsDropdownOpen(false);
      
      if (result.success) {
        alert('ออกจากระบบเรียบร้อยแล้ว');
        navigate('/', { replace: true });
      }
    } catch (error) {
      console.error('Logout error:', error);
      alert('เกิดข้อผิดพลาดในการออกจากระบบ');
    }
  };

  // ฟังก์ชันไปหน้า profile
  const handleProfile = () => {
    setIsDropdownOpen(false);
    navigate('/profile');
  };

  // ฟังก์ชันไปหน้า Admin Dashboard
  const handleAdminDashboard = () => {
    setIsDropdownOpen(false);
    navigate('/admin/dashboard');
  };

  // ฟังก์ชันไปหน้า HR Dashboard
  const handleHRDashboard = () => {
    setIsDropdownOpen(false);
    navigate('/hr/dashboard');
  };

  // ตรวจสอบว่าเป็น Admin หรือไม่
  const isAdmin = () => {
    return user && (user.user_type === 'Admin' || (user.roles && user.roles.includes('Admin')));
  };

  // ตรวจสอบว่าเป็น HR หรือไม่
  const isHR = () => {
    return user && (user.user_type === 'HR' || (user.roles && user.roles.includes('HR')));
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <a href="/" className="navbar-logo">
          InternScreen
        </a>
        
        <div className="navbar-menu">
          <div className="navbar-links">
            <a href="/" className="navbar-link">หน้าหลัก</a>
            <a href="/companies" className="navbar-link">บริษัททั้งหมด</a>
          </div>
          
          <div className="navbar-auth">
            {isAuthenticated() ? (
              // แสดง User Menu เมื่อ login แล้ว
              <div className="user-menu" ref={dropdownRef}>
                <button 
                  className="user-button"
                  onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                  disabled={profileLoading}
                >
                  {/* Profile Image หรือ Avatar */}
                  <div className="user-avatar">
                    {profileImageUrl ? (
                      <img 
                        src={profileImageUrl} 
                        alt="Profile" 
                        className="user-profile-image"
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                    ) : null}
                    <div 
                      className="user-avatar-fallback"
                      style={{ 
                        display: profileImageUrl ? 'none' : 'flex' 
                      }}
                    >
                      {profileLoading ? '...' : initials}
                    </div>
                  </div>
                  
                  {/* ชื่อผู้ใช้ */}
                  <span className="user-name">
                    {profileLoading ? 'กำลังโหลด...' : displayName}
                  </span>
                  
                  {/* ลูกศรสำหรับ dropdown */}
                  <svg 
                    className={`dropdown-arrow ${isDropdownOpen ? 'open' : ''}`}
                    width="16" 
                    height="16" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor"
                  >
                    <polyline points="6,9 12,15 18,9"></polyline>
                  </svg>
                </button>

                {/* Dropdown Menu */}
                {isDropdownOpen && (
                  <div className="dropdown-menu">
                    <div className="dropdown-header">
                      <div className="dropdown-avatar">
                        {profileImageUrl ? (
                          <img 
                            src={profileImageUrl} 
                            alt="Profile" 
                            className="dropdown-profile-image"
                            onError={(e) => {
                              e.target.style.display = 'none';
                              e.target.nextSibling.style.display = 'flex';
                            }}
                          />
                        ) : null}
                        <div 
                          className="dropdown-avatar-fallback"
                          style={{ 
                            display: profileImageUrl ? 'none' : 'flex' 
                          }}
                        >
                          {profileLoading ? '...' : initials}
                        </div>
                      </div>
                      <div className="dropdown-user-info">
                        <p className="dropdown-name">
                          {profileLoading ? 'กำลังโหลด...' : (fullName || 'User')}
                        </p>
                        <p className="dropdown-email">
                          {profileLoading ? 'กำลังโหลด...' : (email || 'user@example.com')}
                        </p>
                      </div>
                    </div>

                    <div className="dropdown-divider"></div>

                    <div className="dropdown-body">
                      <button 
                        className="dropdown-item"
                        onClick={handleProfile}
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
                          <circle cx="12" cy="7" r="4"/>
                        </svg>
                        โปรไฟล์ส่วนตัว
                      </button>

                      {/* แสดงเมนู Admin เฉพาะ Admin */}
                      {isAdmin() && (
                        <button 
                          className="dropdown-item"
                          onClick={handleAdminDashboard}
                        >
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                            <rect x="3" y="3" width="18" height="18" rx="2" ry="2"/>
                            <rect x="9" y="9" width="6" height="6"/>
                            <path d="M9 1v6"/>
                            <path d="M21 9h-6"/>
                            <path d="M9 23v-6"/>
                            <path d="M3 9h6"/>
                          </svg>
                          จัดการระบบ (Admin)
                        </button>
                      )}

                      {/* แสดงเมนู HR เฉพาะ HR */}
                      {isHR() && (
                        <button 
                          className="dropdown-item"
                          onClick={handleHRDashboard}
                        >
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                            <path d="M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"/>
                            <circle cx="8.5" cy="7" r="4"/>
                            <path d="M20 8v6"/>
                            <path d="M23 11h-6"/>
                          </svg>
                          แดชบอร์ด HR
                        </button>
                      )}

                      <button 
                        className="dropdown-item logout"
                        onClick={handleLogout}
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                          <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"/>
                          <polyline points="16,17 21,12 16,7"/>
                          <line x1="21" y1="12" x2="9" y2="12"/>
                        </svg>
                        ออกจากระบบ
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              // แสดงปุ่ม Login/Register เมื่อยังไม่ login
              <>
                <button 
                  className="auth-btn login-btn"
                  onClick={() => navigate('/login')}
                >
                  เข้าสู่ระบบ
                </button>
                <button 
                  className="auth-btn register-btn"
                  onClick={() => navigate('/register')}
                >
                  สมัครสมาชิก
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
