// frontend/src/components/HR/CreateJobForm.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateJobForm.css';

const CreateJobForm = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    // ข้อมูลพื้นฐาน
    title: '',
    description: '',
    short_description: '',
    
    // ประเภทการฝึกงาน
    internship_type: 'full_time',
    department: 'IT_SOFTWARE',
    work_mode: 'onsite',
    
    // สถานที่และเวลา
    location: '',
    address: '',
    nearby_stations: [],
    duration_months: 3,
    start_date: '',
    end_date: '',
    working_days: ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'],
    working_hours: '08:00-17:00',
    flexible_hours: false,
    
    // คุณสมบัติ
    student_levels: ['BACHELOR_3', 'BACHELOR_4'],
    majors: ['ทุกสาขา'],
    min_gpa: '',
    requirements: [],
    skills_required: [],
    preferred_skills: [],
    
    // ค่าตอบแทน
    compensation_type: 'paid',
    compensation_amount: '',
    compensation_description: '',
    
    // สวัสดิการ
    welfare_lunch: false,
    welfare_transport: false,
    welfare_accommodation: false,
    welfare_medical: false,
    welfare_training: true,
    welfare_others: [],
    
    // สถิติ
    positions_available: 1,
    
    // วันที่
    application_deadline: '',
    
    // AI Features
    auto_screening: false,
    min_matching_score: 0.6,
    
    // Tags
    tags: [],
    is_urgent: false,
    is_featured: false
  });

  const [skillInput, setSkillInput] = useState('');
  const [preferredSkillInput, setPreferredSkillInput] = useState('');
  const [tagInput, setTagInput] = useState('');
  const [requirementInput, setRequirementInput] = useState('');
  const [stationInput, setStationInput] = useState('');
  const [majorInput, setMajorInput] = useState('');

  // ตัวเลือกต่างๆ
  const internshipTypes = [
    { value: 'full_time', label: 'เต็มเวลา' },
    { value: 'part_time', label: 'ชั่วคราว' },
    { value: 'project_based', label: 'ตามโปรเจค' },
    { value: 'summer', label: 'ฝึกงานภาคฤดูร้อน' },
    { value: 'co_op', label: 'สหกิจศึกษา' }
  ];

  const departments = [
    { value: 'IT_SOFTWARE', label: 'IT - Software Development' },
    { value: 'IT_DATA', label: 'IT - Data Analysis' },
    { value: 'IT_NETWORK', label: 'IT - Network & Infrastructure' },
    { value: 'IT_CYBERSECURITY', label: 'IT - Cybersecurity' },
    { value: 'IT_SUPPORT', label: 'IT - Technical Support' },
    { value: 'MARKETING', label: 'การตลาด' },
    { value: 'SALES', label: 'ฝ่ายขาย' },
    { value: 'HR', label: 'ทรัพยากรบุคคล' },
    { value: 'FINANCE', label: 'การเงิน' },
    { value: 'ACCOUNTING', label: 'บัญชี' },
    { value: 'GRAPHIC_DESIGN', label: 'ออกแบบกราฟิก' },
    { value: 'UI_UX', label: 'UI/UX Design' },
    { value: 'CONTENT_CREATION', label: 'สร้างคอนเทนต์' },
    { value: 'SOCIAL_MEDIA', label: 'Social Media' },
    { value: 'OPERATIONS', label: 'ปฏิบัติการ' },
    { value: 'LOGISTICS', label: 'โลจิสติกส์' },
    { value: 'PROCUREMENT', label: 'จัดซื้อ' },
    { value: 'QUALITY_CONTROL', label: 'ควบคุมคุณภาพ' },
    { value: 'RESEARCH', label: 'วิจัยและพัฒนา' },
    { value: 'LEGAL', label: 'กฎหมาย' },
    { value: 'PUBLIC_RELATIONS', label: 'ประชาสัมพันธ์' },
    { value: 'OTHER', label: 'อื่นๆ' }
  ];

  const workModes = [
    { value: 'onsite', label: 'ทำงานที่ออฟฟิศ' },
    { value: 'remote', label: 'ทำงานจากที่บ้าน' },
    { value: 'hybrid', label: 'ผสมผสาน' }
  ];

  const compensationTypes = [
    { value: 'paid', label: 'มีเงินเดือน' },
    { value: 'unpaid', label: 'ไม่มีเงินเดือน' },
    { value: 'allowance', label: 'ค่าใช้จ่าย' },
    { value: 'stipend', label: 'ทุนการศึกษา' }
  ];

  const studentLevels = [
    { value: 'VOCATIONAL_1', label: 'ปวช. ปี 1' },
    { value: 'VOCATIONAL_2', label: 'ปวช. ปี 2' },
    { value: 'VOCATIONAL_3', label: 'ปวช. ปี 3' },
    { value: 'DIPLOMA_1', label: 'ปวส. ปี 1' },
    { value: 'DIPLOMA_2', label: 'ปวส. ปี 2' },
    { value: 'BACHELOR_1', label: 'ปริญญาตรี ปี 1' },
    { value: 'BACHELOR_2', label: 'ปริญญาตรี ปี 2' },
    { value: 'BACHELOR_3', label: 'ปริญญาตรี ปี 3' },
    { value: 'BACHELOR_4', label: 'ปริญญาตรี ปี 4' },
    { value: 'MASTER', label: 'ปริญญาโท' }
  ];

  const workingDays = [
    { value: 'monday', label: 'จันทร์' },
    { value: 'tuesday', label: 'อังคาร' },
    { value: 'wednesday', label: 'พุธ' },
    { value: 'thursday', label: 'พฤหัสบดี' },
    { value: 'friday', label: 'ศุกร์' },
    { value: 'saturday', label: 'เสาร์' },
    { value: 'sunday', label: 'อาทิตย์' }
  ];

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleMultiSelectChange = (name, value) => {
    setFormData(prev => ({
      ...prev,
      [name]: prev[name].includes(value) 
        ? prev[name].filter(item => item !== value)
        : [...prev[name], value]
    }));
  };

  const handleArrayAdd = (type, input, setInput) => {
    if (input.trim()) {
      setFormData(prev => ({
        ...prev,
        [type]: [...prev[type], input.trim()]
      }));
      setInput('');
    }
  };

  const handleArrayRemove = (type, index) => {
    setFormData(prev => ({
      ...prev,
      [type]: prev[type].filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      
      // ปรับแต่งข้อมูลก่อนส่ง
      const submitData = {
        ...formData,
        min_gpa: formData.min_gpa ? parseFloat(formData.min_gpa) : null,
        compensation_amount: formData.compensation_amount ? parseInt(formData.compensation_amount) : null,
        skills_required: formData.skills_required.map(skill => ({
          name: skill,
          is_required: true,
          weight: 1.0
        })),
        requirements: formData.requirements,
        welfare_others: []
      };

      const response = await fetch('/api/jobs', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(submitData)
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to create job: ${errorText}`);
      }

      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        const result = await response.json();
        console.log('Job created successfully:', result);
      }

      navigate('/hr/jobs', { 
        state: { message: 'สร้างตำแหน่งงานเรียบร้อยแล้ว' }
      });
    } catch (error) {
      console.error('Error creating job:', error);
      alert(`เกิดข้อผิดพลาดในการสร้างตำแหน่งงาน: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (window.confirm('คุณต้องการยกเลิกการสร้างตำแหน่งงานหรือไม่?')) {
      navigate('/hr/jobs');
    }
  };

  const addSkill = () => {
    handleArrayAdd('skills_required', skillInput, setSkillInput);
  };

  const addPreferredSkill = () => {
    handleArrayAdd('preferred_skills', preferredSkillInput, setPreferredSkillInput);
  };

  const addTag = () => {
    handleArrayAdd('tags', tagInput, setTagInput);
  };

  const addRequirement = () => {
    handleArrayAdd('requirements', requirementInput, setRequirementInput);
  };

  const addStation = () => {
    handleArrayAdd('nearby_stations', stationInput, setStationInput);
  };

  const addMajor = () => {
    handleArrayAdd('majors', majorInput, setMajorInput);
  };

  const handleSkillKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addSkill();
    }
  };

  const handlePreferredSkillKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addPreferredSkill();
    }
  };

  const handleTagKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addTag();
    }
  };

  const handleRequirementKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addRequirement();
    }
  };

  const handleStationKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addStation();
    }
  };

  const handleMajorKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addMajor();
    }
  };

  return (
    <div className="create-job-form">
      <div className="form-header">
        <div className="header-content">
          <h1>สร้างตำแหน่งฝึกงานใหม่</h1>
          <p>กรอกข้อมูลตำแหน่งงานที่ต้องการเปิดรับสมัคร</p>
        </div>
        <div className="header-actions">
          <button 
            type="button" 
            onClick={handleCancel}
            className="secondary-button"
          >
            ยกเลิก
          </button>
          <button 
            type="submit" 
            form="job-form"
            disabled={loading}
            className="primary-button"
          >
            {loading ? 'กำลังบันทึก...' : 'บันทึก'}
          </button>
        </div>
      </div>

      <form id="job-form" onSubmit={handleSubmit} className="job-form">
        {/* ข้อมูลพื้นฐาน */}
        <div className="form-section">
          <div className="section-header">
            <h2>ข้อมูลพื้นฐาน</h2>
            <p>รายละเอียดหลักของตำแหน่งงาน</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label htmlFor="title" className="form-label">
                ชื่อตำแหน่งงาน <span className="required">*</span>
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                className="form-input"
                placeholder="เช่น นักศึกษาฝึกงาน Frontend Developer"
                required
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="short_description" className="form-label">
                คำอธิบายสั้น
              </label>
              <input
                type="text"
                id="short_description"
                name="short_description"
                value={formData.short_description}
                onChange={handleInputChange}
                className="form-input"
                placeholder="สรุปงานสั้นๆ ไม่เกิน 200 ตัวอักษร"
                maxLength="200"
              />
            </div>

            <div className="form-group">
              <label htmlFor="department" className="form-label">
                แผนก <span className="required">*</span>
              </label>
              <select
                id="department"
                name="department"
                value={formData.department}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                {departments.map(dept => (
                  <option key={dept.value} value={dept.value}>
                    {dept.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="internship_type" className="form-label">
                ประเภทการฝึกงาน <span className="required">*</span>
              </label>
              <select
                id="internship_type"
                name="internship_type"
                value={formData.internship_type}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                {internshipTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="work_mode" className="form-label">
                รูปแบบการทำงาน <span className="required">*</span>
              </label>
              <select
                id="work_mode"
                name="work_mode"
                value={formData.work_mode}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                {workModes.map(mode => (
                  <option key={mode.value} value={mode.value}>
                    {mode.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="duration_months" className="form-label">
                ระยะเวลา (เดือน) <span className="required">*</span>
              </label>
              <input
                type="number"
                id="duration_months"
                name="duration_months"
                value={formData.duration_months}
                onChange={handleInputChange}
                className="form-input"
                min="1"
                max="12"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="positions_available" className="form-label">
                จำนวนที่รับ <span className="required">*</span>
              </label>
              <input
                type="number"
                id="positions_available"
                name="positions_available"
                value={formData.positions_available}
                onChange={handleInputChange}
                className="form-input"
                min="1"
                required
              />
            </div>
          </div>
        </div>

        {/* สถานที่และเวลา */}
        <div className="form-section">
          <div className="section-header">
            <h2>สถานที่และเวลาทำงาน</h2>
            <p>ข้อมูลเกี่ยวกับสถานที่และเวลาการทำงาน</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label htmlFor="location" className="form-label">
                สถานที่ทำงาน <span className="required">*</span>
              </label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                className="form-input"
                placeholder="เช่น กรุงเทพฯ, เชียงใหม่, หรือ Remote"
                required
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="address" className="form-label">
                ที่อยู่เต็ม
              </label>
              <input
                type="text"
                id="address"
                name="address"
                value={formData.address}
                onChange={handleInputChange}
                className="form-input"
                placeholder="ที่อยู่ที่ละเอียด (ถ้ามี)"
              />
            </div>

            <div className="form-group">
              <label htmlFor="working_hours" className="form-label">
                เวลาทำงาน <span className="required">*</span>
              </label>
              <input
                type="text"
                id="working_hours"
                name="working_hours"
                value={formData.working_hours}
                onChange={handleInputChange}
                className="form-input"
                placeholder="08:00-17:00"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">
                <input
                  type="checkbox"
                  name="flexible_hours"
                  checked={formData.flexible_hours}
                  onChange={handleInputChange}
                />
                เวลาทำงานยืดหยุ่น
              </label>
            </div>

            {/* วันทำงาน */}
            <div className="form-group full-width">
              <label className="form-label">
                วันทำงาน <span className="required">*</span>
              </label>
              <div className="checkbox-group">
                {workingDays.map(day => (
                  <label key={day.value} className="checkbox-label">
                    <input
                      type="checkbox"
                      checked={formData.working_days.includes(day.value)}
                      onChange={() => handleMultiSelectChange('working_days', day.value)}
                    />
                    {day.label}
                  </label>
                ))}
              </div>
            </div>

            {/* สถานีที่ใกล้ */}
            <div className="form-group full-width">
              <label className="form-label">สถานีที่ใกล้เคียง</label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={stationInput}
                  onChange={(e) => setStationInput(e.target.value)}
                  className="form-input"
                  placeholder="เช่น สถานี BTS อโศก"
                  onKeyPress={handleStationKeyPress}
                />
                <button
                  type="button"
                  onClick={addStation}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.nearby_stations.map((station, index) => (
                  <div key={index} className="skill-tag">
                    <span>{station}</span>
                    <button
                      type="button"
                      onClick={() => handleArrayRemove('nearby_stations', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* คำอธิบายงาน */}
        <div className="form-section">
          <div className="section-header">
            <h2>คำอธิบายงาน</h2>
            <p>รายละเอียดเกี่ยวกับงานและหน้าที่ความรับผิดชอบ</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label htmlFor="description" className="form-label">
                คำอธิบายงาน <span className="required">*</span>
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                className="form-textarea"
                rows="6"
                placeholder="อธิบายลักษณะงาน หน้าที่ความรับผิดชอบ และสิ่งที่นักศึกษาจะได้เรียนรู้..."
                required
              />
            </div>
          </div>
        </div>

        {/* คุณสมบัติผู้สมัคร */}
        <div className="form-section">
          <div className="section-header">
            <h2>คุณสมบัติผู้สมัคร</h2>
            <p>ระบุคุณสมบัติที่ต้องการจากผู้สมัคร</p>
          </div>
          
          <div className="form-grid">
            {/* ระดับการศึกษา */}
            <div className="form-group full-width">
              <label className="form-label">
                ระดับการศึกษา <span className="required">*</span>
              </label>
              <div className="checkbox-group">
                {studentLevels.map(level => (
                  <label key={level.value} className="checkbox-label">
                    <input
                      type="checkbox"
                      checked={formData.student_levels.includes(level.value)}
                      onChange={() => handleMultiSelectChange('student_levels', level.value)}
                    />
                    {level.label}
                  </label>
                ))}
              </div>
            </div>

            {/* สาขาวิชา */}
            <div className="form-group full-width">
              <label className="form-label">สาขาวิชา</label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={majorInput}
                  onChange={(e) => setMajorInput(e.target.value)}
                  className="form-input"
                  placeholder="เช่น วิทยาการคอมพิวเตอร์, ทุกสาขา"
                  onKeyPress={handleMajorKeyPress}
                />
                <button
                  type="button"
                  onClick={addMajor}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.majors.map((major, index) => (
                  <div key={index} className="skill-tag">
                    <span>{major}</span>
                    <button
                      type="button"
                      onClick={() => handleArrayRemove('majors', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="min_gpa" className="form-label">
                เกรดเฉลี่ยขั้นต่ำ
              </label>
              <input
                type="number"
                id="min_gpa"
                name="min_gpa"
                value={formData.min_gpa}
                onChange={handleInputChange}
                className="form-input"
                placeholder="2.50"
                min="0"
                max="4"
                step="0.01"
              />
            </div>

            {/* ข้อกำหนดเพิ่มเติม */}
            <div className="form-group full-width">
              <label className="form-label">ข้อกำหนดเพิ่มเติม</label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={requirementInput}
                  onChange={(e) => setRequirementInput(e.target.value)}
                  className="form-input"
                  placeholder="เช่น มีประสบการณ์ในการเขียนโปรแกรม"
                  onKeyPress={handleRequirementKeyPress}
                />
                <button
                  type="button"
                  onClick={addRequirement}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.requirements.map((req, index) => (
                  <div key={index} className="skill-tag">
                    <span>{req}</span>
                    <button
                      type="button"
                      onClick={() => handleArrayRemove('requirements', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* ทักษะที่ต้องการ */}
        <div className="form-section">
          <div className="section-header">
            <h2>ทักษะที่ต้องการ</h2>
            <p>ระบุทักษะที่จำเป็นและทักษะที่ต้องการเพิ่มเติม</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group full-width">
              <label className="form-label">
                ทักษะที่จำเป็น <span className="required">*</span>
              </label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={skillInput}
                  onChange={(e) => setSkillInput(e.target.value)}
                  className="form-input"
                  placeholder="เช่น JavaScript, React, Node.js"
                  onKeyPress={handleSkillKeyPress}
                />
                <button
                  type="button"
                  onClick={addSkill}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.skills_required.map((skill, index) => (
                  <div key={index} className="skill-tag">
                    <span>{skill}</span>
                    <button
                      type="button"
                      onClick={() => handleArrayRemove('skills_required', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group full-width">
              <label className="form-label">ทักษะที่ต้องการเพิ่มเติม</label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={preferredSkillInput}
                  onChange={(e) => setPreferredSkillInput(e.target.value)}
                  className="form-input"
                  placeholder="เช่น TypeScript, Docker, AWS"
                  onKeyPress={handlePreferredSkillKeyPress}
                />
                <button
                  type="button"
                  onClick={addPreferredSkill}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.preferred_skills.map((skill, index) => (
                  <div key={index} className="skill-tag preferred">
                    <span>{skill}</span>
                    <button
                      type="button"
                      onClick={() => handleArrayRemove('preferred_skills', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* ค่าตอบแทนและสวัสดิการ */}
        <div className="form-section">
          <div className="section-header">
            <h2>ค่าตอบแทนและสวัสดิการ</h2>
            <p>ข้อมูลเกี่ยวกับค่าตอบแทนและสวัสดิการที่ให้</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="compensation_type" className="form-label">
                ประเภทค่าตอบแทน <span className="required">*</span>
              </label>
              <select
                id="compensation_type"
                name="compensation_type"
                value={formData.compensation_type}
                onChange={handleInputChange}
                className="form-select"
                required
              >
                {compensationTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>

            {(formData.compensation_type === 'paid' || formData.compensation_type === 'allowance') && (
              <div className="form-group">
                <label htmlFor="compensation_amount" className="form-label">
                  จำนวนเงิน (บาท/เดือน)
                </label>
                <input
                  type="number"
                  id="compensation_amount"
                  name="compensation_amount"
                  value={formData.compensation_amount}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="15000"
                  min="0"
                />
              </div>
            )}

            <div className="form-group full-width">
              <label htmlFor="compensation_description" className="form-label">
                รายละเอียดค่าตอบแทน
              </label>
              <textarea
                id="compensation_description"
                name="compensation_description"
                value={formData.compensation_description}
                onChange={handleInputChange}
                className="form-textarea"
                rows="3"
                placeholder="เช่น รวมค่าเดินทาง, ค่าอาหาร, หรือสวัสดิการอื่นๆ"
              />
            </div>

            {/* สวัสดิการ */}
            <div className="form-group full-width">
              <label className="form-label">สวัสดิการ</label>
              <div className="checkbox-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="welfare_lunch"
                    checked={formData.welfare_lunch}
                    onChange={handleInputChange}
                  />
                  ค่าอาหาร
                </label>
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="welfare_transport"
                    checked={formData.welfare_transport}
                    onChange={handleInputChange}
                  />
                  ค่าเดินทาง
                </label>
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="welfare_accommodation"
                    checked={formData.welfare_accommodation}
                    onChange={handleInputChange}
                  />
                  ที่พัก
                </label>
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="welfare_medical"
                    checked={formData.welfare_medical}
                    onChange={handleInputChange}
                  />
                  ประกันสุขภาพ
                </label>
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="welfare_training"
                    checked={formData.welfare_training}
                    onChange={handleInputChange}
                  />
                  การฝึกอบรม
                </label>
              </div>
            </div>
          </div>
        </div>

        {/* วันที่สำคัญ */}
        <div className="form-section">
          <div className="section-header">
            <h2>วันที่สำคัญ</h2>
            <p>กำหนดวันที่เริ่มต้น สิ้นสุด และหมดเขตรับสมัคร</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="application_deadline" className="form-label">
                วันหมดเขตรับสมัคร
              </label>
              <input
                type="date"
                id="application_deadline"
                name="application_deadline"
                value={formData.application_deadline}
                onChange={handleInputChange}
                className="form-input"
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="start_date" className="form-label">
                วันที่เริ่มฝึกงาน
              </label>
              <input
                type="date"
                id="start_date"
                name="start_date"
                value={formData.start_date}
                onChange={handleInputChange}
                className="form-input"
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="end_date" className="form-label">
                วันที่สิ้นสุดฝึกงาน
              </label>
              <input
                type="date"
                id="end_date"
                name="end_date"
                value={formData.end_date}
                onChange={handleInputChange}
                className="form-input"
                min={formData.start_date || new Date().toISOString().split('T')[0]}
              />
            </div>
          </div>
        </div>

        {/* การตั้งค่า AI และอื่นๆ */}
        <div className="form-section">
          <div className="section-header">
            <h2>การตั้งค่าเพิ่มเติม</h2>
            <p>การตั้งค่า AI และป้ายกำกับต่างๆ</p>
          </div>
          
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">
                <input
                  type="checkbox"
                  name="auto_screening"
                  checked={formData.auto_screening}
                  onChange={handleInputChange}
                />
                เปิดใช้การคัดกรองอัตโนมัติด้วย AI
              </label>
            </div>

            {formData.auto_screening && (
              <div className="form-group">
                <label htmlFor="min_matching_score" className="form-label">
                  คะแนนจับคู่ขั้นต่ำ (0.0-1.0)
                </label>
                <input
                  type="number"
                  id="min_matching_score"
                  name="min_matching_score"
                  value={formData.min_matching_score}
                  onChange={handleInputChange}
                  className="form-input"
                  min="0"
                  max="1"
                  step="0.1"
                />
              </div>
            )}

            <div className="form-group">
              <label className="form-label">
                <input
                  type="checkbox"
                  name="is_urgent"
                  checked={formData.is_urgent}
                  onChange={handleInputChange}
                />
                รับสมัครด่วน
              </label>
            </div>

            <div className="form-group">
              <label className="form-label">
                <input
                  type="checkbox"
                  name="is_featured"
                  checked={formData.is_featured}
                  onChange={handleInputChange}
                />
                โปรโมทพิเศษ
              </label>
            </div>

            {/* Tags */}
            <div className="form-group full-width">
              <label className="form-label">ป้ายกำกับ (Tags)</label>
              <div className="skill-input-container">
                <input
                  type="text"
                  value={tagInput}
                  onChange={(e) => setTagInput(e.target.value)}
                  className="form-input"
                  placeholder="เช่น startup, tech, remote-friendly"
                  onKeyPress={handleTagKeyPress}
                />
                <button
                  type="button"
                  onClick={addTag}
                  className="add-skill-button"
                >
                  เพิ่ม
                </button>
              </div>
              <div className="skills-list">
                {formData.tags.map((tag, index) => (
                  <div key={index} className="skill-tag tag">
                    <span>{tag}</span>
                    <button
                      type="button"
                      onClick={() => handleArrayRemove('tags', index)}
                      className="remove-skill-button"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default CreateJobForm;
