// frontend/src/components/HR/CompanyList.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/CompanyList.css';

const CompanyList = () => {
  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterIndustry, setFilterIndustry] = useState('all');
  const [filterSize, setFilterSize] = useState('all');
  const navigate = useNavigate();

  useEffect(() => {
    fetchCompanies();
  }, []);

  const fetchCompanies = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/companies', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        console.log('API not available, using mock data');
        setCompanies([]); // ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô array ‡∏ß‡πà‡∏≤‡∏á
        return;
      }

      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        const data = await response.json();
        setCompanies(data.companies || []);
      } else {
        console.log('Non-JSON response, using mock data');
        setCompanies([]);
      }
    } catch (err) {
      console.error('Error fetching companies:', err);
      setCompanies([]);
    } finally {
      setLoading(false);
    }
  };

  // ‡∏Å‡∏£‡∏≠‡∏á‡πÅ‡∏•‡∏∞‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó
  const filteredCompanies = companies.filter(company => {
    const matchesSearch = company.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         company.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesIndustry = filterIndustry === 'all' || company.industry === filterIndustry;
    const matchesSize = filterSize === 'all' || company.size === filterSize;
    
    return matchesSearch && matchesIndustry && matchesSize;
  });

  // ‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ü‡∏¥‡∏•‡πÄ‡∏ï‡∏≠‡∏£‡πå
  const industries = [...new Set(companies.map(c => c.industry).filter(Boolean))];
  const sizes = [...new Set(companies.map(c => c.size).filter(Boolean))];

  const handleViewCompany = (companyId) => {
    navigate(`/hr/companies/${companyId}`);
  };

  const handleViewJobs = (companyId) => {
    navigate(`/hr/companies/${companyId}/jobs`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">
          <h3>‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î</h3>
          <p>{error}</p>
          <button onClick={fetchCompanies} className="retry-button">
            ‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="company-list">
      <div className="page-header">
        <div className="header-content">
          <h1>‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡πÉ‡∏ô‡∏£‡∏∞‡∏ö‡∏ö</h1>
          <p>‡∏î‡∏π‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏ï‡πà‡∏≤‡∏á‡πÜ ‡πÅ‡∏•‡∏∞‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡∏¥‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</p>
        </div>
      </div>

      {/* Search and Filters */}
      <div className="search-filters">
        <div className="search-bar">
          <div className="search-input-container">
            <input
              type="text"
              placeholder="‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            <div className="search-icon">üîç</div>
          </div>
        </div>

        <div className="filter-controls">
          <div className="filter-group">
            <label htmlFor="industry-filter">‡∏≠‡∏∏‡∏ï‡∏™‡∏≤‡∏´‡∏Å‡∏£‡∏£‡∏°:</label>
            <select
              id="industry-filter"
              value={filterIndustry}
              onChange={(e) => setFilterIndustry(e.target.value)}
              className="filter-select"
            >
              <option value="all">‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</option>
              {industries.map(industry => (
                <option key={industry} value={industry}>
                  {industry}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label htmlFor="size-filter">‡∏Ç‡∏ô‡∏≤‡∏î‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó:</label>
            <select
              id="size-filter"
              value={filterSize}
              onChange={(e) => setFilterSize(e.target.value)}
              className="filter-select"
            >
              <option value="all">‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</option>
              {sizes.map(size => (
                <option key={size} value={size}>
                  {size}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Results Summary */}
      <div className="results-summary">
        <p>‡∏û‡∏ö {filteredCompanies.length} ‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó</p>
      </div>

      {/* Companies Grid */}
      <div className="companies-grid">
        {filteredCompanies.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üè¢</div>
            <h3>‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏ó‡∏µ‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö‡πÄ‡∏á‡∏∑‡πà‡∏≠‡∏ô‡πÑ‡∏Ç</h3>
            <p>‡∏•‡∏≠‡∏á‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏Ñ‡∏≥‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏´‡∏£‡∏∑‡∏≠‡∏ü‡∏¥‡∏•‡πÄ‡∏ï‡∏≠‡∏£‡πå</p>
          </div>
        ) : (
          filteredCompanies.map(company => (
            <div key={company.id} className="company-card">
              <div className="company-header">
                <div className="company-logo">
                  {company.logo_url ? (
                    <img src={company.logo_url} alt={company.name} />
                  ) : (
                    <div className="logo-placeholder">
                      {company.name.charAt(0).toUpperCase()}
                    </div>
                  )}
                </div>
                <div className="company-basic-info">
                  <h3 className="company-name">{company.name}</h3>
                  <div className="company-meta">
                    <span className="company-industry">{company.industry}</span>
                    {company.size && (
                      <span className="company-size">{company.size}</span>
                    )}
                  </div>
                </div>
              </div>

              <div className="company-description">
                <p>{company.description || '‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ñ‡∏≥‡∏≠‡∏ò‡∏¥‡∏ö‡∏≤‡∏¢‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏ï‡∏¥‡∏°'}</p>
              </div>

              <div className="company-details">
                <div className="detail-item">
                  <span className="detail-label">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà:</span>
                  <span className="detail-value">
                    {company.address || '‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏'}
                  </span>
                </div>
                
                {company.website && (
                  <div className="detail-item">
                    <span className="detail-label">‡πÄ‡∏ß‡πá‡∏ö‡πÑ‡∏ã‡∏ï‡πå:</span>
                    <a 
                      href={company.website} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="detail-link"
                    >
                      {company.website}
                    </a>
                  </div>
                )}

                {company.phone && (
                  <div className="detail-item">
                    <span className="detail-label">‡πÇ‡∏ó‡∏£‡∏®‡∏±‡∏û‡∏ó‡πå:</span>
                    <span className="detail-value">{company.phone}</span>
                  </div>
                )}

                {company.email && (
                  <div className="detail-item">
                    <span className="detail-label">‡∏≠‡∏µ‡πÄ‡∏°‡∏•:</span>
                    <span className="detail-value">{company.email}</span>
                  </div>
                )}
              </div>

              <div className="company-stats">
                <div className="stat-item">
                  <span className="stat-value">{company.active_positions || 0}</span>
                  <span className="stat-label">‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÄ‡∏õ‡∏¥‡∏î</span>
                </div>
                <div className="stat-item">
                  <span className="stat-value">{company.hr_count || 0}</span>
                  <span className="stat-label">HR</span>
                </div>
                <div className="stat-item">
                  <span className="stat-value">{company.total_applications || 0}</span>
                  <span className="stat-label">‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</span>
                </div>
              </div>

              <div className="company-actions">
                <button 
                  onClick={() => handleViewCompany(company.id)}
                  className="primary-button"
                >
                  ‡∏î‡∏π‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î
                </button>
                <button 
                  onClick={() => handleViewJobs(company.id)}
                  className="secondary-button"
                >
                  ‡∏î‡∏π‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô ({company.active_positions || 0})
                </button>
              </div>

              {company.featured && (
                <div className="featured-badge">
                  ‚≠ê ‡πÅ‡∏ô‡∏∞‡∏ô‡∏≥
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CompanyList;
