    authService.setCurrentUser(updatedUser);
  };

  // ðŸŽ¯ à¸Ÿà¸±à¸‡à¸à¹Œà¸Šà¸±à¸™ sync à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¹‚à¸›à¸£à¹„à¸Ÿà¸¥à¹Œà¸¥à¹ˆà¸²à¸ªà¸¸à¸”
  const syncProfile = async () => {
    if (!isAuthenticated()) return;
    
    try {
      const profileData = await profileService.getProfile();
      const updatedUser = {
        ...user,
        full_name: profileData.full_name,
        email: profileData.email,
        phone: profileData.phone,
        profile_image: profileData.profile_image,
        updated_at: profileData.updated_at
      };
      setUser(updatedUser);
      authService.setCurrentUser(updatedUser);
      return updatedUser;
    } catch (error) {
      console.error('Failed to sync profile:', error);
      throw error;
    }
  };

  // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸šà¸§à¹ˆà¸² user login à¸­à¸¢à¸¹à¹ˆà¸«à¸£à¸·à¸­à¹„à¸¡à¹ˆ
  const isAuthenticated = () => {
    return user !== null && authService.getToken() !== null;
  };

  // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸š role à¸‚à¸­à¸‡à¸œà¸¹à¹‰à¹ƒà¸Šà¹‰
  const hasRole = (role) => {
    if (!user) return false;
    return user.user_type === role || (user.roles && user.roles.includes(role));
  };

  // ðŸŽ¯ à¸Ÿà¸±à¸‡à¸à¹Œà¸Šà¸±à¸™à¸Šà¹ˆà¸§à¸¢à¹€à¸«à¸¥à¸·à¸­à¸ªà¸³à¸«à¸£à¸±à¸šà¹‚à¸›à¸£à¹„à¸Ÿà¸¥à¹Œ
  const getInitials = (fullName) => {
    if (!fullName) return 'U';
    
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return (names[0][0] + names[1][0]).toUpperCase();
    }
    return names[0][0].toUpperCase();
  };

  const getDisplayName = (fullName) => {
    if (!fullName) return 'User';
    
    const names = fullName.split(' ');
    if (names.length >= 2) {
      return names[0]; // à¹à¸ªà¸”à¸‡à¹à¸„à¹ˆà¸Šà¸·à¹ˆà¸­
    }
    return fullName;
  };

  const getProfileImageUrl = () => {
    const imageUrl = user?.profile_image;
    
    if (imageUrl) {
      // à¸–à¹‰à¸²à¹€à¸›à¹‡à¸™ URL à¹€à¸•à¹‡à¸¡ à¹ƒà¸Šà¹‰à¹€à¸¥à¸¢
      if (imageUrl.startsWith('http')) {
        return imageUrl;
      }
      // à¸–à¹‰à¸²à¹€à¸›à¹‡à¸™ path à¸ªà¸±à¸¡à¸žà¸±à¸—à¸˜à¹Œ à¹€à¸žà¸´à¹ˆà¸¡ base URL
      return `http://localhost:8000${imageUrl}`;
    }
    
    return null;
  };

  // Context value
  const value = {
    // Core auth data
    user,
    loading,
    
    // Auth methods
    isAuthenticated,
    hasRole,
    login,
    register,
    logout,
    
    // Profile methods
    updateUser,
    syncProfile,
    
    // Helper methods
    getInitials,
    getDisplayName,
    getProfileImageUrl,
    
    // Computed properties
    hasProfileImage: !!getProfileImageUrl(),
    fullName: user?.full_name || '',
    email: user?.email || '',
    userType: user?.user_type || ''
  };

  // Loading Component
  const LoadingSpinner = ({ size = 'medium', message = 'à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”...' }) => (
    <div className="auth-loading" style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '200px',
      gap: '16px'
    }}>
      <div 
        className="spinner"
        style={{
          width: size === 'large' ? '48px' : '32px',
          height: size === 'large' ? '48px' : '32px',
          border: '3px solid #f3f3f3',
          borderTop: '3px solid #4F46E5',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite'
        }}
      />
      <p style={{ color: '#6B7280', fontSize: '14px' }}>{message}</p>
      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );

  return (
    <AuthContext.Provider value={value}>
      {loading ? (
        <LoadingSpinner size="large" message="à¸à¸³à¸¥à¸±à¸‡à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸šà¸ªà¸–à¸²à¸™à¸°à¸à¸²à¸£à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸š..." />
      ) : (
        children
      )}
    </AuthContext.Provider>
  );
};

export default AuthContext;
